      scrapeFrequency: 48
    },
    {
      name: 'City of Calgary Council',
      url: 'https://www.calgary.ca/council/councillors.html',
      type: 'municipal',
      dataTypes: ['politicians', 'council_records'],
      scrapeFrequency: 48
    },
    {
      name: 'City of Ottawa Council',
