import { Pool } from 'pg';
import { drizzle } from 'drizzle-orm/node-postgres';
import * as baseSchema from '../shared/schema.js';
import * as identitySchema from '../shared/identity-schema.js';
import pino from "pino";

const logger = pino();

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

// Parse connection string for paranoid logging
function parseDbUrl(url: string) {
  try {
    const u = new URL(url);
    return {
      host: u.hostname,
      port: u.port,
      user: u.username,
      database: u.pathname.replace(/^\//, ''),
      ssl: u.search.includes('sslmode') ? u.search : 'default',
    };
  } catch {
    return { host: 'unknown', port: 'unknown', user: 'unknown', database: 'unknown', ssl: 'unknown' };
  }
}

const dbInfo = parseDbUrl(process.env.DATABASE_URL);
logger.info('[DB] Connecting to:', {
  host: dbInfo.host,
  port: dbInfo.port,
  user: dbInfo.user,
  database: dbInfo.database,
  ssl: dbInfo.ssl,
  sslConfig: { rejectUnauthorized: false },
});

// SSL configuration - disable for local development
const sslConfig = process.env.NODE_ENV === 'production' ? {
  rejectUnauthorized: false,
  checkServerIdentity: () => undefined
} : false; // Disable SSL for local development

export const pool = new Pool({ 
  connectionString: process.env.DATABASE_URL,
  ssl: sslConfig
});

// Paranoid: Drizzle does not accept 'ssl' in config, so we rely on Pool's SSL config only
// Merge schemas so Drizzle knows about all tables/types
const combinedSchema = { ...baseSchema, ...identitySchema } as any;
export const db = drizzle(pool, { schema: combinedSchema });
// Paranoid logging: log Pool type and SSL env (do NOT log credentials)
logger.info('[DB] Drizzle instantiated. Pool type:', typeof pool, 'NODE_TLS_REJECT_UNAUTHORIZED:', process.env.NODE_TLS_REJECT_UNAUTHORIZED);

// Paranoid: Test DB connection at startup (non-blocking)
setTimeout(async () => {
  try {
    const result = await pool.query('SELECT NOW() as now');
    logger.info('[DB] Connection test successful. Time:', result.rows[0].now);
  } catch (err: any) {
    logger.error('[DB] Connection test FAILED:', err);
    if (err && typeof err === 'object' && err !== null && 'code' in err && (err as any).code === 'SELF_SIGNED_CERT_IN_CHAIN') {
      logger.error('[DB] SSL self-signed certificate error. Check your DATABASE_URL and SSL config.');
    }
    // Don't exit in production, just log the error
    if (process.env.NODE_ENV !== 'production') {
      process.exit(1);
    }
  }
}, 10000); // 10 second delay to ensure server is fully started

