            IF NOT EXISTS (
              SELECT 1 FROM information_schema.columns 
              WHERE table_name = 'politicians' AND column_name = ${column}
            ) THEN
              ALTER TABLE politicians ADD COLUMN ${sql.raw(column)} text;
            END IF;
          END $$;
