      try {
        await storage.createBill({
          billNumber: bill.number,
          title: bill.title,
          description: bill.summary,
          fullText: "",
          category: inferCategory(bill.title),
          jurisdiction: "Federal",
          status: bill.status,
          votingDeadline: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000)
        });
