    stripePriceIdYearly: 'price_gov_yearly',
  },
];

export async function createCustomer(email: string, name: string) {
  const stripeInstance = await initializeStripe();
  if (!stripeInstance) {
    throw new Error('Stripe is not configured. Please set STRIPE_SECRET_KEY environment variable.');
  }

  try {
    const customer = await stripeInstance.customers.create({
      email,
      name,
      metadata: {
        source: 'civicos'
      }
    });
    return customer;
  } catch (error) {
    // console.error removed for production
    throw error;
  }
}

export async function createSubscription(customerId: string, priceId: string) {
  const stripeInstance = await initializeStripe();
  if (!stripeInstance) {
    throw new Error('Stripe is not configured. Please set STRIPE_SECRET_KEY environment variable.');
  }

  try {
    const subscription = await stripeInstance.subscriptions.create({
      customer: customerId,
      items: [{ price: priceId }],
      payment_behavior: 'default_incomplete',
      payment_settings: { save_default_payment_method: 'on_subscription' },
      expand: ['latest_invoice.payment_intent'],
    });
    return subscription;
  } catch (error) {
    // console.error removed for production
    throw error;
  }
}

export async function createCheckoutSession(customerId: string, priceId: string, successUrl: string, cancelUrl: string) {
  const stripeInstance = await initializeStripe();
  if (!stripeInstance) {
    throw new Error('Stripe is not configured. Please set STRIPE_SECRET_KEY environment variable.');
  }

  try {
    const session = await stripeInstance.checkout.sessions.create({
      customer: customerId,
      payment_method_types: ['card'],
      line_items: [
        {
          price: priceId,
          quantity: 1,
        },
      ],
      mode: 'subscription',
      success_url: successUrl,
      cancel_url: cancelUrl,
      metadata: {
        source: 'civicos'
      }
    });
    return session;
  } catch (error) {
    // console.error removed for production
    throw error;
  }
}

export async function getSubscription(subscriptionId: string) {
  const stripeInstance = await initializeStripe();
  if (!stripeInstance) {
    throw new Error('Stripe is not configured. Please set STRIPE_SECRET_KEY environment variable.');
  }

  try {
    const subscription = await stripeInstance.subscriptions.retrieve(subscriptionId);
    return subscription;
  } catch (error) {
    // console.error removed for production
    throw error;
  }
}

export async function cancelSubscription(subscriptionId: string) {
  const stripeInstance = await initializeStripe();
  if (!stripeInstance) {
    throw new Error('Stripe is not configured. Please set STRIPE_SECRET_KEY environment variable.');
  }

  try {
    const subscription = await stripeInstance.subscriptions.cancel(subscriptionId);
    return subscription;
  } catch (error) {
    // console.error removed for production
    throw error;
  }
}

export async function updateSubscription(subscriptionId: string, newPriceId: string) {
  const stripeInstance = await initializeStripe();
  if (!stripeInstance) {
    throw new Error('Stripe is not configured. Please set STRIPE_SECRET_KEY environment variable.');
  }

  try {
    const subscription = await stripeInstance.subscriptions.retrieve(subscriptionId);
    const updatedSubscription = await stripeInstance.subscriptions.update(subscriptionId, {
      items: [
        {
          id: subscription.items.data[0].id,
          price: newPriceId,
        },
      ],
    });
    return updatedSubscription;
  } catch (error) {
    // console.error removed for production
    throw error;
  }
}

export async function createPaymentIntent(amount: number, currency: string = 'cad') {
  const stripeInstance = await initializeStripe();
  if (!stripeInstance) {
    throw new Error('Stripe is not configured. Please set STRIPE_SECRET_KEY environment variable.');
  }

  try {
    const paymentIntent = await stripeInstance.paymentIntents.create({
      amount: Math.round(amount * 100), // Convert to cents
      currency,
      metadata: {
        source: 'civicos'
      }
    });
    return paymentIntent;
  } catch (error) {
    // console.error removed for production
    throw error;
  }
}

export async function getCustomer(customerId: string) {
  const stripeInstance = await initializeStripe();
  if (!stripeInstance) {
    throw new Error('Stripe is not configured. Please set STRIPE_SECRET_KEY environment variable.');
  }

  try {
    const customer = await stripeInstance.customers.retrieve(customerId);
    return customer;
  } catch (error) {
    // console.error removed for production
    throw error;
  }
}

export async function updateCustomer(customerId: string, data: any) {
  const stripeInstance = await initializeStripe();
  if (!stripeInstance) {
    throw new Error('Stripe is not configured. Please set STRIPE_SECRET_KEY environment variable.');
  }

  try {
    const customer = await stripeInstance.customers.update(customerId, data);
    return customer;
  } catch (error) {
    // console.error removed for production
    throw error;
  }
}

