              <FloatingChatButton />
              <FloatingMessageButton />
              <Toaster />

              {/* Tutorial */}
              {showTutorial && (
                <FeatureTutorial onComplete={handleTutorialComplete} />
              )}
            </div>
          </Router>
        </AuthProvider>
      </QueryClientProvider>
    </ErrorBoundary>
  );
}

