import { Express, Request, Response } from "express";
import { ResponseFormatter } from "../utils/responseFormatter.js";
import jwt from "jsonwebtoken";

// JWT Auth middleware
function jwtAuth(req: any, res: any, next: any) {
  const authHeader = req.headers.authorization;
  if (!authHeader || !authHeader.startsWith("Bearer ")) {
    return ResponseFormatter.unauthorized(res, "Missing or invalid token");
  }

  try {
    const token = authHeader.split(" ")[1];
    const secret = process.env.SESSION_SECRET;
    if (!secret) {
      return ResponseFormatter.unauthorized(res, "Server configuration error");
    }
    const decoded = jwt.verify(token, secret);
    req.user = decoded;
    next();
  } catch (err) {
    return ResponseFormatter.unauthorized(res, "Invalid or expired token");
  }
}

export function registerLedgerRoutes(app: Express) {
  // Get ledger entries
  app.get('/api/ledger', async (req: Request, res: Response) => {
    const startTime = Date.now();

    try {
      // For now, return empty data - will be populated by real database integration
      const ledgerEntries: any[] = [];

      const processingTime = Date.now() - startTime;
      return ResponseFormatter.success(
        res,
        { entries: ledgerEntries },
        "Ledger data retrieved successfully",
        200,
        ledgerEntries.length,
        undefined,
        processingTime
