        200,
        undefined,
        undefined,
        processingTime
      );
    } catch (error) {
      return ResponseFormatter.databaseError(res, `Failed to fetch civic ledger data: ${(error as Error).message}`);
    }
  });

  // Get ledger statistics
  app.get('/api/ledger/stats', async (req: Request, res: Response) => {
    const startTime = Date.now();

    try {
      const stats = {
        totalEntries: 4,
