          tags: ['governance', 'transparency', 'whistleblower'],
          createdAt: new Date(),
          updatedAt: new Date()
        },
        {
          title: 'Increase Funding for Mental Health Services',
          description: 'Petition to allocate more federal funding for mental health services and make them more accessible to all Canadians.',
          category: 'healthcare',
          targetSignatures: 20000,
          currentSignatures: 0,
          status: 'active',
          creatorId: 'system',
          jurisdiction: 'federal',
          tags: ['healthcare', 'mental-health', 'funding'],
          createdAt: new Date(),
          updatedAt: new Date()
        }
      ];

      await db.insert(petitions).values(samplePetitions);
      logger.info('Sample petitions created');
    } catch (error) {
      logger.error('Failed to create sample petitions:', error);
      throw error;
    }
  }

  async getIngestionStatus(): Promise<IngestionStatus> {
    try {
      const [politiciansCount] = await db.select({ count: count() }).from(politicians);
      const [billsCount] = await db.select({ count: count() }).from(bills);
      const [electionsCount] = await db.select({ count: count() }).from(elections);
      const [legalActsCount] = await db.select({ count: count() }).from(legalActs);
      const [procurementCount] = await db.select({ count: count() }).from(procurementContracts);
      const [lobbyistsCount] = await db.select({ count: count() }).from(lobbyistOrgs);
      const [newsCount] = await db.select({ count: count() }).from(newsArticles);
      const [petitionsCount] = await db.select({ count: count() }).from(petitions);

      return {
        politicians: { success: true, message: `${politiciansCount.count} politicians`, timestamp: new Date().toISOString() },
        bills: { success: true, message: `${billsCount.count} bills`, timestamp: new Date().toISOString() },
