  votingRecord: { yes: number; no: number; abstain: number };
  contactInfo: {
    email?: string;
    phone?: string;
    office?: string;
    website?: string;
    social?: {
      twitter?: string;
      facebook?: string;
    };
  };
  bio: string;
  keyAchievements: string[];
