  fullText?: string;
  amendments?: number;
}

interface EconomicData {
  gdp: {
    current: number;
    growth: number;
    quarterly: number[];
  };
  inflation: {
    current: number;
    monthly: number[];
    target: number;
  };
  unemployment: {
    current: number;
    monthly: number[];
    byProvince: { [key: string]: number };
  };
  currency: {
    cadUsd: number;
    cadEur: number;
    cadGbp: number;
  };
  bankRate: number;
  housingPrice: {
    average: number;
    byCity: { [key: string]: number };
    yearOverYear: number;
  };
  lastUpdated: string;
}

interface NewsData {
  id: string;
  title: string;
  summary: string;
  source: string;
  sourceId?: number;
  url?: string;
  publishedAt: string;
  category: string;
  region?: string;
  credibility?: number;
  bias?: string;
  readTime?: number;
  image?: string;
  tags?: string[];
  verified?: boolean;
  publishedDate?: string; // Keep for backward compatibility
  credibilityScore?: number; // Keep for backward compatibility
  sentiment?: string; // Keep for backward compatibility
  topics?: string[]; // Keep for backward compatibility
}

