client/src/components/CivicChatBot.tsx.content:
import React, { useState, useRef, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Badge } from "@/components/ui/badge";
import { X, Send, Bot, User, Loader2, Sparkles, Shield, TrendingUp } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { aiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface Message {
  id: string;
  role: 'user' | 'assistant';
  content: string;
  timestamp: Date;
  analysisType?: 'bill' | 'politician' | 'general';
  confidence?: number;
  sources?: string[];
  truthScore?: number;
  propagandaRisk?: 'low' | 'medium' | 'high';
  followUpSuggestions?: string[];
}

interface ChatbotProps {
  isOpen: boolean;
  onClose: () => void;
}

export function CivicChatBot({ isOpen, onClose }: ChatbotProps) {
  const [messages, setMessages] = useState<Message[]>([
    {
      id: '1',
      role: 'assistant',
      content: "Hello! I'm CivicOS, your Canadian civic intelligence assistant. I can help you analyze legislation, track politicians, explain your rights, and guide civic actions. What would you like to know?",
      timestamp: new Date(),
      analysisType: 'general',
      confidence: 1.0,
      sources: ['CivicOS System'],
      truthScore: 1.0,
      propagandaRisk: 'low',
      followUpSuggestions: [
        "Tell me about the latest bills in Parliament",
        "Who is my MP and how can I contact them?",
        "What are my rights as a Canadian citizen?",
        "How do I file a complaint about government services?"
      ]
    }
  ]);
  const [input, setInput] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const scrollAreaRef = useRef<HTMLDivElement>(null);
  const { user } = useAuth();
  const { toast } = useToast();

  // Auto-scroll to bottom when new messages arrive
  useEffect(() => {
    if (scrollAreaRef.current) {
      const scrollArea = scrollAreaRef.current;
      scrollArea.scrollTop = scrollArea.scrollHeight;
    }
  }, [messages]);

  // Persist messages in localStorage
  useEffect(() => {
    const savedMessages = localStorage.getItem('civicos-chat-messages');
    if (savedMessages) {
      try {
        const parsed = JSON.parse(savedMessages);
        setMessages(parsed.map((msg: any) => ({
          ...msg,
          timestamp: new Date(msg.timestamp)
        })));
      } catch (error) {
        // console.error removed for production
      }
    }
  }, []);

  // Save messages to localStorage whenever they change
  useEffect(() => {
    if (messages.length > 1) { // Don't save just the initial message
      localStorage.setItem('civicos-chat-messages', JSON.stringify(messages));
    }
  }, [messages]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!input.trim() || isLoading) return;

    const userMessage: Message = {
      id: Date.now().toString(),
      role: 'user',
      content: input.trim(),
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInput("");
    setIsLoading(true);

    try {
      const response = await aiRequest('/api/ai/chat', 'POST', {
        message: input.trim(),
        context: {
          userLocation: (user as any)?.city ? `${(user as any).city}, ${(user as any).province}` : undefined,
          userInterests: ['civic engagement', 'Canadian politics'],
          previousMessages: messages.slice(-5).map(msg => msg.content)
        }
      });

      const assistantMessage: Message = {
        id: (Date.now() + 1).toString(),
        role: 'assistant',
        content: response.message || response.response || "I'm sorry, I couldn't generate a response at this time.",
        timestamp: new Date(),
        analysisType: 'general',
        confidence: 0.9,
        sources: ['CivicOS AI'],
        truthScore: 0.9,
        propagandaRisk: 'low',
        followUpSuggestions: [
          "Ask me about Canadian politics",
          "Learn about your rights",
          "Find your local representatives"
        ]
      };

      setMessages(prev => [...prev, assistantMessage]);
    } catch (error: any) {
      // console.error removed for production

      const errorMessage: Message = {
        id: (Date.now() + 1).toString(),
        role: 'assistant',
        content: "I'm sorry, I'm having trouble connecting to my AI service right now. Please try again in a moment, or contact support if the issue persists.",
        timestamp: new Date(),
        analysisType: 'general',
        confidence: 0.0,
        sources: ['CivicOS System'],
        truthScore: 0.0,
        propagandaRisk: 'low'
      };

      setMessages(prev => [...prev, errorMessage]);

      toast({
        title: "Chat Error",
        description: "Failed to get AI response. Please try again.",
        variant: "destructive"
      });
    } finally {
      setIsLoading(false);
    }
  };

  const getConfidenceColor = (confidence?: number) => {
    if (!confidence) return "bg-gray-500";
    if (confidence >= 0.8) return "bg-green-500";
    if (confidence >= 0.6) return "bg-yellow-500";
    return "bg-red-500";
  };

  const getPropagandaColor = (risk?: string) => {
    if (!risk) return "bg-gray-500";
    if (risk === 'low') return "bg-green-500";
    if (risk === 'medium') return "bg-yellow-500";
    return "bg-red-500";
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <Card className="w-full max-w-2xl h-[80vh] flex flex-col bg-white shadow-xl">
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-4">
          <div className="flex items-center space-x-2">
            <Bot className="h-5 w-5 text-blue-600" />
            <CardTitle>CivicOS AI Assistant</CardTitle>
            <Badge variant="secondary" className="text-xs">
              {user ? 'Authenticated' : 'Guest'}
            </Badge>
          </div>
          <Button variant="ghost" size="sm" onClick={onClose}>
            <X className="h-4 w-4" />
          </Button>
        </CardHeader>

        <CardContent className="p-0">
          <ScrollArea 
            ref={scrollAreaRef}
            className="h-[400px] w-full p-4"
            style={{ scrollBehavior: 'smooth' }}
          >
            <div className="space-y-4">
              {messages.map((message) => (
                <div
                  key={message.id}
                  className={`flex ${message.role === 'user' ? 'justify-end' : 'justify-start'}`}
                >
                  <div
                    className={`max-w-[80%] rounded-lg p-3 ${
                      message.role === 'user'
                        ? 'bg-blue-600 text-white'
                        : 'bg-gray-100 dark:bg-gray-800 text-gray-900 dark:text-gray-100'
                    }`}
                  >
                    <div className="flex items-start gap-2">
                      {message.role === 'assistant' && (
                        <Bot className="w-4 h-4 mt-0.5 flex-shrink-0" />
                      )}
                      <div className="flex-1">
                        <div className="whitespace-pre-wrap text-sm">{message.content}</div>
                        {message.followUpSuggestions && message.followUpSuggestions.length > 0 && (
                          <div className="mt-3 space-y-2">
                            <div className="text-xs text-gray-500 dark:text-gray-400">
                              Suggested follow-ups:
                            </div>
                            <div className="flex flex-wrap gap-2">
                              {message.followUpSuggestions.slice(0, 3).map((suggestion, index) => (
                                <Button
                                  key={index}
                                  size="sm"
                                  variant="outline"
                                  className="text-xs h-6 px-2"
                                  onClick={() => setInput(suggestion)}
                                >
                                  {suggestion}
                                </Button>
                              ))}
                            </div>
                          </div>
                        )}
                      </div>
                      {message.role === 'user' && (
                        <User className="w-4 h-4 mt-0.5 flex-shrink-0" />
                      )}
                    </div>
                    <div className="text-xs opacity-70 mt-1">
                      {message.timestamp.toLocaleTimeString()}
                    </div>
                  </div>
                </div>
              ))}
              {isLoading && (
                <div className="flex justify-start">
                  <div className="bg-gray-100 dark:bg-gray-800 rounded-lg p-3">
                    <div className="flex items-center gap-2">
                      <Bot className="w-4 h-4" />
                      <div className="flex items-center gap-1">
                        <Loader2 className="w-3 h-3 animate-spin" />
                        <span className="text-sm">Thinking...</span>
                      </div>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </ScrollArea>

          <form onSubmit={handleSubmit} className="flex space-x-2">
            <Input
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Ask about bills, politicians, your rights..."
              disabled={isLoading}
              className="flex-1"
            />
            <Button type="submit" disabled={isLoading || !input.trim()}>
              {isLoading ? (
                <Loader2 className="h-4 w-4 animate-spin" />
              ) : (
                <Send className="h-4 w-4" />
              )}
            </Button>
          </form>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/CivicSocialCard.ts.content:
import React from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { cn } from '@/lib/utils';

interface CivicSocialCardProps {
  title?: string;
  description?: string;
  children: React.ReactNode;
  className?: string;
  variant?: 'default' | 'elevated' | 'interactive' | 'compact';
  onClick?: () => void;
  loading?: boolean;
  hover?: boolean;
}

export function CivicSocialCard({
  title,
  description,
  children,
  className,
  variant = 'default',
  onClick,
  loading = false,
  hover = true
}: CivicSocialCardProps) {
  const variants = {
    default: "social-card",
    elevated: "social-card-elevated",
    interactive: "social-card-interactive",
    compact: "social-card p-4"
  };

  const hoverClass = hover ? "social-hover-lift" : "";

  return (
    <Card 
      className={cn(
        variants[variant],
        hoverClass,
        loading && "animate-pulse",
        className
      )}
      onClick={onClick}
    >
      {(title || description) && (
        <CardHeader className="pb-3">
          {title && (
            <CardTitle className="social-heading-3 text-card-foreground">
              {title}
            </CardTitle>
          )}
          {description && (
            <CardDescription className="social-caption">
              {description}
            </CardDescription>
          )}
        </CardHeader>
      )}
      <CardContent className={cn(
        "space-y-4",
        !title && !description && "pt-6"
      )}>
        {children}
      </CardContent>
    </Card>
  );
}

interface CivicSocialPostCardProps {
  post: {
    id: number;
    content: string;
    imageUrl?: string;
    createdAt: string;
    user?: {
      firstName?: string;
      lastName?: string;
      profileImageUrl?: string;
      civicLevel?: string;
    };
    likesCount: number;
    commentsCount: number;
    sharesCount: number;
    isLiked: boolean;
    isBookmarked: boolean;
  };
  onLike?: (postId: number) => void;
  onComment?: (postId: number) => void;
  onShare?: (postId: number) => void;
  onBookmark?: (postId: number) => void;
  onEdit?: (postId: number) => void;
  onDelete?: (postId: number) => void;
  className?: string;
}

export function CivicSocialPostCard({
  post,
  onLike,
  onComment,
  onShare,
  onBookmark,
  onEdit,
  onDelete,
  className
}: CivicSocialPostCardProps) {
  const displayName = post.user ? 
    (post.user.firstName && post.user.lastName ? 
      `${post.user.firstName} ${post.user.lastName}` : 
      post.user.firstName || post.user.lastName || 'Anonymous') : 
    'Anonymous';

  const formatTimeAgo = (dateString: string) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInSeconds = Math.floor((now.getTime() - date.getTime()) / 1000);

    if (diffInSeconds < 60) return 'just now';
    if (diffInSeconds < 3600) return `${Math.floor(diffInSeconds / 60)}m ago`;
    if (diffInSeconds < 86400) return `${Math.floor(diffInSeconds / 3600)}h ago`;
    if (diffInSeconds < 2592000) return `${Math.floor(diffInSeconds / 86400)}d ago`;
    return date.toLocaleDateString();
  };

  return (
    <CivicSocialCard className={cn("social-fade-in", className)}>
      <div className="flex items-start space-x-3">
        <div className="flex-shrink-0">
          <div className="social-avatar w-10 h-10">
            {post.user?.profileImageUrl ? (
              <img 
                src={post.user.profileImageUrl} 
                alt={displayName}
                className="w-full h-full rounded-full object-cover"
              />
            ) : (
              <div className="w-full h-full rounded-full bg-social-primary flex items-center justify-center text-white font-medium">
                {displayName.charAt(0).toUpperCase()}
              </div>
            )}
          </div>
        </div>

        <div className="flex-1 min-w-0">
          <div className="flex items-center space-x-2 mb-2">
            <span className="font-medium text-card-foreground">{displayName}</span>
            {post.user?.civicLevel && (
              <span className="social-badge-primary">
                {post.user.civicLevel}
              </span>
            )}
            <span className="social-caption">{formatTimeAgo(post.createdAt)}</span>
          </div>

          <div className="social-body text-card-foreground mb-4">
            {post.content}
          </div>

          {post.imageUrl && (
            <div className="mb-4">
              <img 
                src={post.imageUrl} 
                alt="Post content"
                className="w-full rounded-lg object-cover max-h-96"
              />
            </div>
          )}

          <div className="flex items-center justify-between pt-3 border-t border-border">
            <div className="flex items-center space-x-4">
              <button
                onClick={() => onLike?.(post.id)}
                className={cn(
                  "flex items-center space-x-1 text-sm transition-colors",
                  post.isLiked ? "like-color" : "text-muted-foreground hover:like-color"
                )}
              >
                <Heart className={cn("w-4 h-4", post.isLiked && "fill-current")} />
                <span>{post.likesCount}</span>
              </button>

              <button
                onClick={() => onComment?.(post.id)}
                className="flex items-center space-x-1 text-sm text-muted-foreground hover:comment-color transition-colors"
              >
                <MessageCircle className="w-4 h-4" />
                <span>{post.commentsCount}</span>
              </button>

              <button
                onClick={() => onShare?.(post.id)}
                className="flex items-center space-x-1 text-sm text-muted-foreground hover:share-color transition-colors"
              >
                <Share2 className="w-4 h-4" />
                <span>{post.sharesCount}</span>
              </button>
            </div>

            <div className="flex items-center space-x-3">
              <button
                onClick={() => onBookmark?.(post.id)}
                className={cn(
                  "text-sm transition-colors",
                  post.isBookmarked ? "bookmark-color" : "text-muted-foreground hover:bookmark-color"
                )}
              >
                <Bookmark className={cn("w-4 h-4", post.isBookmarked && "fill-current")} />
              </button>
              {onEdit && (
                <button onClick={() => onEdit(post.id)} className="text-sm text-muted-foreground hover:text-blue-600">
                  Edit
                </button>
              )}
              {onDelete && (
                <button onClick={() => onDelete(post.id)} className="text-sm text-red-600 hover:text-red-700">
                  Delete
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
    </CivicSocialCard>
  );
}

interface CivicSocialProfileCardProps {
  user: {
    id: string;
    firstName?: string;
    lastName?: string;
    email: string;
    profileImageUrl?: string;
    civicLevel?: string;
    bio?: string;
    isOnline?: boolean;
  };
  stats?: {
    posts: number;
    friends: number;
    civicPoints: number;
  };
  onMessage?: (userId: string) => void;
  onFollow?: (userId: string) => void;
  className?: string;
}

export function CivicSocialProfileCard({
  user,
  stats,
  onMessage,
  onFollow,
  className
}: CivicSocialProfileCardProps) {
  const displayName = user.firstName && user.lastName ? 
    `${user.firstName} ${user.lastName}` : 
    user.firstName || user.lastName || user.email || 'Anonymous';

  return (
    <CivicSocialCard className={cn("social-scale-in", className)}>
      <div className="flex flex-col items-center text-center space-y-4">
        <div className="relative">
          <div className={cn(
            "social-avatar w-20 h-20",
            user.isOnline ? "social-avatar-online" : "social-avatar-offline"
          )}>
            {user.profileImageUrl ? (
              <img 
                src={user.profileImageUrl} 
                alt={displayName}
                className="w-full h-full rounded-full object-cover"
              />
            ) : (
              <div className="w-full h-full rounded-full bg-social-primary flex items-center justify-center text-white font-bold text-xl">
                {displayName.charAt(0).toUpperCase()}
              </div>
            )}
          </div>

          {user.isOnline && (
            <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-online rounded-full border-2 border-card"></div>
          )}
        </div>

        <div>
          <h3 className="social-heading-2 text-card-foreground">{displayName}</h3>
          {user.civicLevel && (
            <span className="social-badge-primary mt-1">
              {user.civicLevel}
            </span>
          )}
        </div>

        {user.bio && (
          <p className="social-body text-muted-foreground max-w-md">
            {user.bio}
          </p>
        )}

        {stats && (
          <div className="flex space-x-6 pt-2">
            <div className="text-center">
              <div className="social-heading-3 text-card-foreground">{stats.posts}</div>
              <div className="social-caption">Posts</div>
            </div>
            <div className="text-center">
              <div className="social-heading-3 text-card-foreground">{stats.friends}</div>
              <div className="social-caption">Friends</div>
            </div>
            <div className="text-center">
              <div className="social-heading-3 text-card-foreground">{stats.civicPoints}</div>
              <div className="social-caption">Points</div>
            </div>
          </div>
        )}

        <div className="flex space-x-3 pt-2">
          {onMessage && (
            <button
              onClick={() => onMessage(user.id)}
              className="social-button-primary px-4 py-2 rounded-md"
            >
              Message
            </button>
          )}
          {onFollow && (
            <button
              onClick={() => onFollow(user.id)}
              className="social-button-secondary px-4 py-2 rounded-md"
            >
              Follow
            </button>
          )}
        </div>
      </div>
    </CivicSocialCard>
  );
}

