import React, { useState } from "react";
import { Bell, User, MessageCircle, Heart, Share2, Users } from "lucide-react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "./ui/button";
import { Card } from "./ui/card";
import { Badge } from "./ui/badge";
import { useAuth } from "../hooks/useAuth";
import { createCivicOsClient } from "@/lib/civicos-sdk-wrapper";
import { useLocation } from "wouter";

interface Notification {
  id: string | number;
  type: string;
  title: string;
  message: string;
  sourceModule?: string;
  sourceId?: string;
  timestamp?: string;
  createdAt?: string;
  read?: boolean;
}

export default function NotificationBell() {
  const { user } = useAuth();
  const [, navigate] = useLocation();
  const queryClient = useQueryClient();
  const [open, setOpen] = useState(false);
  const client = createCivicOsClient();
  const { data: notifications = [] } = useQuery<Notification[]>({
    queryKey: ["/api/notifications"],
    queryFn: async () => {
      const res = await client.request({ method: 'GET', url: '/api/notifications' });
      return Array.isArray(res) ? (res as any) : ((res as any)?.notifications || []);
    },
    enabled: !!user,
  });
  const { data: unreadData } = useQuery<{ unread: number }>({
    queryKey: ["/api/notifications/unread-count"],
    queryFn: async () => {
      const res = await client.request({ method: 'GET', url: '/api/notifications/unread-count' });
      return res as any;
    },
    enabled: !!user,
  });
  const unread = (notifications as any[]).filter((n: any) => !n.read && !n.isRead);
  const unreadCount = unreadData?.unread ?? unread.length;

  const markAsReadMutation = useMutation({
    mutationFn: async (id: number) => {
      await client.request({ method: 'PATCH', url: `/api/notifications/${id}/read` });
      return id;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/notifications"] });
      queryClient.invalidateQueries({ queryKey: ["/api/notifications/unread-count"] });
    },
  });

  const markAllAsReadMutation = useMutation({
    mutationFn: async () => {
      await client.request({ method: 'PATCH', url: `/api/notifications/read-all` });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/notifications"] });
      queryClient.invalidateQueries({ queryKey: ["/api/notifications/unread-count"] });
    },
  });

  // Keyboard navigation for bell and dropdown
  function handleBellKeyDown(e: React.KeyboardEvent<HTMLDivElement>) {
    if (e.key === "Enter" || e.key === " ") {
      setOpen((prev) => !prev);
    } else if (e.key === "Escape") {
      setOpen(false);
    }
  }

  function handleNotificationKeyDown(e: React.KeyboardEvent<HTMLLIElement>, notif: Notification) {
    if (e.key === "Enter" || e.key === " ") {
      handleNotificationClick(notif);
    }
  }

  function handleNotificationClick(notif: Notification) {
    if (!(notif as any).read && !(notif as any).isRead) markAsReadMutation.mutate(Number(notif.id));
    // Optionally navigate or show details
  }

  function markAllAsRead() {
    markAllAsReadMutation.mutate();
  }

  function formatTimeAgo(dateString?: string) {
    if (!dateString) return "";
    const date = new Date(dateString);
    const now = new Date();
    const diff = Math.floor((now.getTime() - date.getTime()) / 1000);
    if (diff < 60) return `${diff}s ago`;
    if (diff < 3600) return `${Math.floor(diff / 60)}m ago`;
    if (diff < 86400) return `${Math.floor(diff / 3600)}h ago`;
    return date.toLocaleDateString();
  }

  return (
    <div className="relative" tabIndex={0} aria-label="Notification bell" onKeyDown={handleBellKeyDown}>
      <button
        className="relative p-2 rounded-full hover:bg-accent focus:outline-none focus:ring-2 focus:ring-primary"
        aria-label="Open notifications"
        onClick={() => setOpen((prev) => !prev)}
      >
        <span className="sr-only">Open notifications</span>
        <Bell className="w-6 h-6" aria-hidden="true" />
        {unreadCount > 0 && (
          <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full px-1.5 py-0.5" aria-label={`${unread.length} unread notifications`}>
            {unreadCount}
          </span>
        )}
      </button>
      {open && (
        <div
          className="absolute right-0 mt-2 w-80 max-w-xs bg-white border rounded shadow-lg z-50 focus:outline-none"
          role="menu"
          aria-label="Notifications dropdown"
          tabIndex={-1}
        >
          <div className="p-2 border-b flex items-center justify-between">
            <span className="font-semibold">Notifications</span>
            <button
              className="text-xs text-primary underline focus:outline-none"
              onClick={markAllAsRead}
              aria-label="Mark all as read"
            >
              Mark all as read
            </button>
          </div>
          <div className="p-2 border-b flex items-center justify-center">
            <a
              href="/civicsocial/feed"
              className="inline-flex items-center gap-2 px-3 py-1.5 rounded bg-blue-50 hover:bg-blue-100 text-blue-700 font-medium text-xs focus:outline-none focus:ring-2 focus:ring-blue-400 transition"
              tabIndex={0}
              aria-label="Go to CivicSocial"
            >
              <Users className="w-4 h-4" />
              CivicSocial
            </a>
          </div>
          <ul className="max-h-72 overflow-y-auto divide-y" role="listbox">
            {notifications.length === 0 && (
              <li className="p-4 text-center text-muted-foreground" role="option" aria-disabled="true">
                No notifications.
              </li>
            )}
             {(notifications as any[]).map((notif: any) => {
              // Determine icon
              let icon = null;
              if (notif.type === 'friend_request') icon = <User className="w-5 h-5 text-primary" aria-hidden="true" />;
              else if (notif.type === 'comment') icon = <MessageCircle className="w-5 h-5 text-primary" aria-hidden="true" />;
              else if (notif.type === 'like') icon = <Heart className="w-5 h-5 text-primary" aria-hidden="true" />;
              else if (notif.type === 'share') icon = <Share2 className="w-5 h-5 text-primary" aria-hidden="true" />;
              else if (notif.type === 'trending' || notif.title?.toLowerCase().includes('trending')) icon = <Badge className="w-5 h-5 bg-pink-100 text-pink-700">ðŸ”¥</Badge>;
              else if (notif.type === 'friend') icon = <Users className="w-5 h-5 text-green-600" aria-hidden="true" />;
              // Determine link
              let link = null;
              if (notif.sourceModule === 'CivicSocial' && notif.sourceId) link = `/civicsocial/feed#post-${notif.sourceId}`;
              // Render notification
              return (
                <li
                  key={notif.id}
                  className={`p-3 flex items-start gap-2 cursor-pointer hover:bg-accent focus:bg-accent outline-none ${notif.read || notif.isRead ? '' : 'bg-blue-50'}`}
                  tabIndex={0}
                  role="option"
                   aria-selected={!notif.read && !notif.isRead}
                  onClick={() => { handleNotificationClick(notif); if (link) navigate(link); }}
                  onKeyDown={e => handleNotificationKeyDown(e, notif)}
                >
                  <span className="flex-shrink-0 mt-1">{icon}</span>
                  <div className="flex-1">
                    <div className="text-sm font-semibold">{notif.title}</div>
                    <div className="text-xs text-muted-foreground mt-1">{notif.message}</div>
                    <div className="text-xs text-muted-foreground mt-1">{formatTimeAgo(notif.createdAt || notif.timestamp)}</div>
                  </div>
                   {!(notif.read || notif.isRead) && <span className="ml-2 w-2 h-2 rounded-full bg-primary" aria-label="Unread notification"></span>}
                </li>
              );
            })}
          </ul>
        </div>
      )}
    </div>
  );
}

client/src/components/NotificationsCente.content:
import React, { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  Bell, 
  Check, 
  CheckCheck, 
  X, 
  Filter,
  Settings,
  MessageCircle,
  Heart,
  Share2,
  User,
  Award,
  Shield,
  Globe,
  Building,
  FileText,
  Vote,
  Users,
  Calendar,
  MapPin
} from "lucide-react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { formatDistanceToNow } from "date-fns";

interface Notification {
  id: number;
  type: string;
  title: string;
  message: string;
  sourceModule: string;
  sourceId?: string;
  isRead: boolean;
  createdAt: string;
}

interface NotificationStats {
  total: number;
  unreadCount: number;
}

export function NotificationsCenter() {
  const { isAuthenticated } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [activeTab, setActiveTab] = useState("all");
  const [showUnreadOnly, setShowUnreadOnly] = useState(false);

  // Fetch notifications
  const { data: notifications = [], isLoading } = useQuery<Notification[]>({
    queryKey: ["notifications", showUnreadOnly],
    queryFn: async () => {
      const response = await apiRequest(`/api/notifications`, "GET");
      // server returns array or { success, notifications }
      return Array.isArray(response) ? response : (response?.notifications || []);
    },
    enabled: isAuthenticated,
  });

  // Mark notification as read
  const markReadMutation = useMutation({
    mutationFn: async (notificationId: number) => {
      // Centralized notifications API (server/simpleNotifications.ts)
      return apiRequest(`/api/notifications/${notificationId}/read`, "PATCH");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["notifications"] });
    },
  });

  // Mark all notifications as read
  const markAllReadMutation = useMutation({
    mutationFn: async () => {
      return apiRequest("/api/notifications/read-all", "PATCH");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["notifications"] });
      toast({
        title: "All notifications marked as read",
        description: "All notifications have been marked as read.",
      });
    },
  });

  // Filter notifications by type
  const filteredNotifications = notifications.filter(notification => {
    if (activeTab === "all") return true;
    return notification.type === activeTab;
  });

  // Get notification icon based on type
  const getNotificationIcon = (type: string) => {
    switch (type) {
      case "friend_request":
        return <Users className="h-4 w-4" />;
      case "message":
        return <MessageCircle className="h-4 w-4" />;
      case "like":
        return <Heart className="h-4 w-4" />;
      case "comment":
        return <MessageCircle className="h-4 w-4" />;
      case "share":
        return <Share2 className="h-4 w-4" />;
      case "achievement":
        return <Award className="h-4 w-4" />;
      case "verification":
        return <Shield className="h-4 w-4" />;
      case "system":
        return <Bell className="h-4 w-4" />;
      case "vote":
        return <Vote className="h-4 w-4" />;
      case "petition":
        return <FileText className="h-4 w-4" />;
      default:
        return <Bell className="h-4 w-4" />;
    }
  };

  // Get notification color based on type
  const getNotificationColor = (type: string) => {
    switch (type) {
      case "friend_request":
        return "text-blue-600 bg-blue-50";
      case "message":
        return "text-green-600 bg-green-50";
      case "like":
        return "text-red-600 bg-red-50";
      case "comment":
        return "text-purple-600 bg-purple-50";
      case "share":
        return "text-orange-600 bg-orange-50";
      case "achievement":
        return "text-yellow-600 bg-yellow-50";
      case "verification":
        return "text-green-600 bg-green-50";
      case "system":
        return "text-gray-600 bg-gray-50";
      case "vote":
        return "text-indigo-600 bg-indigo-50";
      case "petition":
        return "text-pink-600 bg-pink-50";
      default:
        return "text-gray-600 bg-gray-50";
    }
  };

  const handleMarkRead = (notificationId: number) => {
    markReadMutation.mutate(notificationId);
  };

  const handleMarkAllRead = () => {
    markAllReadMutation.mutate();
  };

  if (!isAuthenticated) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <div className="text-center">
          <Bell className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
          <h3 className="text-lg font-semibold">Sign In Required</h3>
          <p className="text-muted-foreground">Please sign in to view notifications.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto p-6 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold">Notifications</h1>
          <p className="text-muted-foreground">
            Stay updated with your civic engagement activities
          </p>
        </div>
        <div className="flex items-center space-x-2">
          <Button
            variant="outline"
            size="sm"
            onClick={() => setShowUnreadOnly(!showUnreadOnly)}
          >
            <Filter className="h-4 w-4 mr-2" />
            {showUnreadOnly ? "Show All" : "Unread Only"}
          </Button>
          {notifications.some(n => !n.isRead) && (
            <Button
              variant="outline"
              size="sm"
              onClick={handleMarkAllRead}
              disabled={markAllReadMutation.isPending}
            >
              <CheckCheck className="h-4 w-4 mr-2" />
              Mark All Read
            </Button>
          )}
        </div>
      </div>

      {/* Notification Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <Bell className="h-5 w-5 text-muted-foreground" />
              <div>
                <p className="text-sm font-medium">Total</p>
                <p className="text-2xl font-bold">{notifications.length}</p>
              </div>
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <Check className="h-5 w-5 text-green-600" />
              <div>
                <p className="text-sm font-medium">Read</p>
                <p className="text-2xl font-bold text-green-600">
                  {notifications.filter(n => n.isRead).length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <Bell className="h-5 w-5 text-blue-600" />
              <div>
                <p className="text-sm font-medium">Unread</p>
                <p className="text-2xl font-bold text-blue-600">
                  {notifications.filter(n => !n.isRead).length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Notification Filters */}
      <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-4">
        <TabsList className="grid w-full grid-cols-6">
          <TabsTrigger value="all">All</TabsTrigger>
          <TabsTrigger value="friend_request">Friends</TabsTrigger>
          <TabsTrigger value="message">Messages</TabsTrigger>
          <TabsTrigger value="like">Likes</TabsTrigger>
          <TabsTrigger value="comment">Comments</TabsTrigger>
          <TabsTrigger value="system">System</TabsTrigger>
        </TabsList>

        <TabsContent value={activeTab} className="space-y-4">
          {isLoading ? (
            <div className="flex items-center justify-center py-8">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
            </div>
          ) : filteredNotifications.length === 0 ? (
            <Card>
              <CardContent className="flex items-center justify-center py-8">
                <div className="text-center">
                  <Bell className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                  <h3 className="text-lg font-semibold">No Notifications</h3>
                  <p className="text-muted-foreground">
                    {showUnreadOnly 
                      ? "You're all caught up! No unread notifications."
                      : "No notifications yet. Stay active to receive updates!"
                    }
                  </p>
                </div>
              </CardContent>
            </Card>
          ) : (
            <ScrollArea className="h-[500px]">
              <div className="space-y-2">
                {filteredNotifications.map((notification) => (
                  <Card
                    key={notification.id}
                    className={`transition-colors ${
                      !notification.isRead ? "bg-blue-50 border-blue-200" : ""
                    }`}
                  >
                    <CardContent className="p-4">
                      <div className="flex items-start space-x-3">
                        <div className={`p-2 rounded-full ${getNotificationColor(notification.type)}`}>
                          {getNotificationIcon(notification.type)}
                        </div>
                        <div className="flex-1 min-w-0">
                          <div className="flex items-start justify-between">
                            <div className="flex-1">
                              <div className="flex items-center space-x-2 mb-1">
                                <h4 className="font-medium text-sm">{notification.title}</h4>
                                {!notification.isRead && (
                                  <Badge variant="secondary" className="text-xs">
                                    New
                                  </Badge>
                                )}
                              </div>
                              <p className="text-sm text-muted-foreground mb-2">
                                {notification.message}
                              </p>
                              <div className="flex items-center space-x-4 text-xs text-muted-foreground">
                                <span>
                                  {formatDistanceToNow(new Date(notification.createdAt))} ago
                                </span>
                                {notification.sourceModule && (
                                  <span className="capitalize">
                                    {notification.sourceModule}
                                  </span>
                                )}
                              </div>
                            </div>
                            <div className="flex items-center space-x-1">
                              {!notification.isRead && (
                                <Button
                                  variant="ghost"
                                  size="sm"
                                  onClick={() => handleMarkRead(notification.id)}
                                  disabled={markReadMutation.isPending}
                                >
                                  <Check className="h-4 w-4" />
                                </Button>
                              )}
                            </div>
                          </div>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            </ScrollArea>
          )}
        </TabsContent>
      </Tabs>
    </div>
  );
}

