import React, { useState, useRef, useEffect } from "react";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { 
  MessageCircle, 
  Heart, 
  Share2, 
  MoreHorizontal, 
  Edit, 
  Trash2, 
  Reply,
  Send,
  Image as ImageIcon,
  Smile
} from "lucide-react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { formatDistanceToNow } from "date-fns";

interface SocialUser {
  id: string;
  firstName?: string;
  lastName?: string;
  profileImageUrl?: string;
}

interface SocialPost {
  id: number;
  content: string;
  imageUrl?: string;
  type: string;
  originalItemId?: number;
  originalItemType?: string;
  comment?: string;
  createdAt: string;
  updatedAt: string;
  userId: string;
  user?: SocialUser;
  likeCount: number; // normalized from likesCount
  commentCount: number; // normalized from commentsCount
  isLiked?: boolean;
}

interface SocialComment {
  id: number;
  content: string;
  parentCommentId?: number;
  createdAt: string;
  updatedAt: string;
  userId: string;
  firstName?: string;
  lastName?: string;
  profileImageUrl?: string;
  likeCount: number;
  isLiked?: boolean;
}

export function SocialFeed() {
  const { user, isAuthenticated } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [newPostContent, setNewPostContent] = useState("");
  const [newCommentContent, setNewCommentContent] = useState("");
  const [selectedPost, setSelectedPost] = useState<SocialPost | null>(null);
  const [isCreatePostOpen, setIsCreatePostOpen] = useState(false);
  const [isCommentDialogOpen, setIsCommentDialogOpen] = useState(false);
  const [editingPost, setEditingPost] = useState<SocialPost | null>(null);
  const [editingComment, setEditingComment] = useState<SocialComment | null>(null);
  const [replyToComment, setReplyToComment] = useState<SocialComment | null>(null);
  const [imageUrl, setImageUrl] = useState("");
  const textareaRef = useRef<HTMLTextAreaElement>(null);

  // Fetch social posts
  const { data: posts = [], isLoading } = useQuery<SocialPost[]>({
    queryKey: ["civicSocialFeed"],
    queryFn: async () => {
      const response = await apiRequest("/api/social/feed", "GET");
      const raw = response?.feed || [];
      // Normalize server shape to UI expectations
      return raw.map((p: any) => ({
        id: p.id,
        content: p.content,
        imageUrl: p.imageUrl,
        type: p.type,
        createdAt: p.createdAt,
        updatedAt: p.updatedAt,
        userId: p.userId,
        user: p.user,
        likeCount: p.likesCount ?? p.likeCount ?? 0,
        commentCount: p.commentsCount ?? p.commentCount ?? 0,
        isLiked: !!p.isLiked,
      })) as SocialPost[];
    },
    enabled: isAuthenticated,
  });

  // Fetch comments for selected post
  const { data: comments = [] } = useQuery<SocialComment[]>({
    queryKey: ["/api/social/posts", selectedPost?.id, "comments"],
    queryFn: async () => {
      const response = await apiRequest(`/api/social/posts/${selectedPost?.id}/comments`, "GET");
      return response?.comments || [];
    },
    enabled: !!selectedPost && isAuthenticated,
  });

  // Create post mutation
  const createPostMutation = useMutation({
    mutationFn: async (data: { content: string; imageUrl?: string }) => {
      return apiRequest("/api/social/posts", "POST", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["civicSocialFeed"] });
      setNewPostContent("");
      setImageUrl("");
      setIsCreatePostOpen(false);
      toast({
        title: "Post created!",
        description: "Your post has been shared successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error creating post",
        description: error.message || "Failed to create post. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Update post mutation
  const updatePostMutation = useMutation({
    mutationFn: async ({ postId, data }: { postId: number; data: { content: string; imageUrl?: string } }) => {
      return apiRequest(`/api/social/posts/${postId}`, "PUT", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["civicSocialFeed"] });
      setEditingPost(null);
      toast({
        title: "Post updated!",
        description: "Your post has been updated successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error updating post",
        description: error.message || "Failed to update post. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Delete post mutation
  const deletePostMutation = useMutation({
    mutationFn: async (postId: number) => {
      return apiRequest(`/api/social/posts/${postId}`, "DELETE");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["civicSocialFeed"] });
      toast({
        title: "Post deleted!",
        description: "Your post has been deleted successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error deleting post",
        description: error.message || "Failed to delete post. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Create comment mutation
  const createCommentMutation = useMutation({
    mutationFn: async ({ postId, content, parentCommentId }: { postId: number; content: string; parentCommentId?: number }) => {
      return apiRequest(`/api/social/posts/${postId}/comment`, "POST", { content, parentCommentId });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/social/posts", selectedPost?.id, "comments"] });
      setNewCommentContent("");
      setReplyToComment(null);
      toast({
        title: "Comment posted!",
        description: "Your comment has been posted successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error posting comment",
        description: error.message || "Failed to post comment. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Update comment mutation
  const updateCommentMutation = useMutation({
    mutationFn: async ({ commentId, content }: { commentId: number; content: string }) => {
      return apiRequest(`/api/social/comments/${commentId}`, "PUT", { content });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/social/posts", selectedPost?.id, "comments"] });
      setEditingComment(null);
      toast({
        title: "Comment updated!",
        description: "Your comment has been updated successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error updating comment",
        description: error.message || "Failed to update comment. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Delete comment mutation
  const deleteCommentMutation = useMutation({
    mutationFn: async (commentId: number) => {
      return apiRequest(`/api/social/comments/${commentId}`, "DELETE");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/social/posts", selectedPost?.id, "comments"] });
      toast({
        title: "Comment deleted!",
        description: "Your comment has been deleted successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error deleting comment",
        description: error.message || "Failed to delete comment. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Like/unlike mutation
  const likeMutation = useMutation({
    mutationFn: async ({ postId }: { postId: number }) => {
      return apiRequest(`/api/social/posts/${postId}/like`, "POST", { reaction: "ðŸ‘" });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["civicSocialFeed"] });
      if (selectedPost) {
        queryClient.invalidateQueries({ queryKey: ["/api/social/posts", selectedPost.id, "comments"] });
      }
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to like/unlike. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Like/unlike comment mutation
  const likeCommentMutation = useMutation({
    mutationFn: async ({ commentId }: { commentId: number }) => {
      return apiRequest(`/api/social/comments/${commentId}/like`, "POST", { reaction: "ðŸ‘" });
    },
    onSuccess: () => {
      if (selectedPost) {
        queryClient.invalidateQueries({ queryKey: ["/api/social/posts", selectedPost.id, "comments"] });
      }
    },
    onError: (error: any) => {
      toast({ title: "Error", description: error?.message || "Failed to like comment.", variant: "destructive" });
    }
  });

  // Share post mutation
  const sharePostMutation = useMutation({
    mutationFn: async ({ postId, platform = 'internal' }: { postId: number; platform?: string }) => {
      return apiRequest(`/api/social/posts/${postId}/share`, 'POST', { platform });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["civicSocialFeed"] });
      toast({ title: 'Shared', description: 'Post shared successfully.' });
    },
    onError: (error: any) => {
      toast({ title: 'Share failed', description: error?.message || 'Could not share post.', variant: 'destructive' });
    }
  });

  const handleCreatePost = () => {
    if (!newPostContent.trim() && !imageUrl.trim()) {
      toast({
        title: "Error",
        description: "Please enter some content or an image URL.",
        variant: "destructive",
      });
      return;
    }
    createPostMutation.mutate({ content: newPostContent, imageUrl: imageUrl || undefined });
  };

  const handleUpdatePost = (post: SocialPost) => {
    if (!editingPost) return;
    updatePostMutation.mutate({
      postId: post.id,
      data: { content: editingPost.content, imageUrl: editingPost.imageUrl }
    });
  };

  const handleDeletePost = (postId: number) => {
    if (confirm("Are you sure you want to delete this post?")) {
      deletePostMutation.mutate(postId);
    }
  };

  const handleCreateComment = () => {
    if (!selectedPost || !newCommentContent.trim()) return;
    createCommentMutation.mutate({
      postId: selectedPost.id,
      content: newCommentContent,
      parentCommentId: replyToComment?.id
    });
  };

  const handleUpdateComment = (comment: SocialComment) => {
    if (!editingComment) return;
    updateCommentMutation.mutate({
      commentId: comment.id,
      content: editingComment.content
    });
  };

  const handleDeleteComment = (commentId: number) => {
    if (confirm("Are you sure you want to delete this comment?")) {
      deleteCommentMutation.mutate(commentId);
    }
  };

  const handleLike = (postId?: number) => {
    if (!postId) return;
    likeMutation.mutate({ postId });
  };

  const getUserDisplayName = (post: SocialPost) => {
    if (post.user?.firstName && post.user?.lastName) {
      return `${post.user.firstName} ${post.user.lastName}`;
    }
    return post.userId;
  };

  const getUserInitials = (post: SocialPost) => {
    if (post.user?.firstName && post.user?.lastName) {
      return `${post.user.firstName[0]}${post.user.lastName[0]}`;
    }
    return post.userId.substring(0, 2).toUpperCase();
  };

  // Build a simple threaded view: group replies under parents
  const buildThread = (items: SocialComment[]) => {
    const byParent = new Map<number | null, SocialComment[]>();
    items.forEach((c) => {
      const key = (c.parentCommentId ?? null) as number | null;
      if (!byParent.has(key)) byParent.set(key, []);
      byParent.get(key)!.push(c);
    });
    return byParent;
  };
  const commentsByParent = buildThread(comments);

  const renderComments = (parentId: number | null = null, depth = 0) => {
    const list = commentsByParent.get(parentId) || [];
    return (
      <div className={depth > 0 ? `ml-6 space-y-3` : `space-y-3`}>
        {list.map((comment) => (
          <div key={comment.id} className="flex space-x-3">
            <Avatar className="h-8 w-8 flex-shrink-0">
              <AvatarImage src={comment.profileImageUrl} />
              <AvatarFallback>
                {comment.firstName?.[0]}{comment.lastName?.[0]}
              </AvatarFallback>
            </Avatar>
            <div className="flex-1">
              <div className="bg-gray-50 p-3 rounded-lg">
                <div className="flex items-center justify-between mb-2">
                  <p className="font-medium text-sm">
                    {comment.firstName && comment.lastName 
                      ? `${comment.firstName} ${comment.lastName}` 
                      : comment.userId}
                  </p>
                  {comment.userId === user?.id && (
                    <div className="flex items-center space-x-1">
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => setEditingComment(comment)}
                      >
                        <Edit className="w-3 h-3" />
                      </Button>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => handleDeleteComment(comment.id)}
                      >
                        <Trash2 className="w-3 h-3" />
                      </Button>
                    </div>
                  )}
                </div>
                {editingComment?.id === comment.id ? (
                  <div className="space-y-2">
                    <Textarea
                      value={editingComment.content}
                      onChange={(e) => setEditingComment({ ...editingComment, content: e.target.value })}
                      className="min-h-[80px]"
                    />
                    <div className="flex space-x-2">
                      <Button size="sm" onClick={() => handleUpdateComment(comment)}>
                        Save
                      </Button>
                      <Button size="sm" variant="outline" onClick={() => setEditingComment(null)}>
                        Cancel
                      </Button>
                    </div>
                  </div>
                ) : (
                  <>
                    <p className="text-sm">{comment.content}</p>
                    <div className="flex items-center space-x-4 mt-2">
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => likeCommentMutation.mutate({ commentId: comment.id })}
                        className={comment.isLiked ? "text-red-500" : ""}
                      >
                        <Heart className={`w-3 h-3 mr-1 ${comment.isLiked ? "fill-current" : ""}`} />
                        {comment.likeCount}
                      </Button>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => setReplyToComment(comment)}
                      >
                        <Reply className="w-3 h-3 mr-1" />
                        Reply
                      </Button>
                    </div>
                  </>
                )}
              </div>
              {/* Render children */}
              {renderComments(comment.id, depth + 1)}
            </div>
          </div>
        ))}
      </div>
    );
  };

  if (!isAuthenticated) {
    return (
      <div className="container mx-auto px-4 py-8">
        <Card>
          <CardContent className="p-6">
            <div className="text-center">
              <h3 className="text-lg font-semibold mb-2">Sign in to view the social feed</h3>
              <p className="text-gray-600">Join the conversation and connect with other citizens.</p>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">CivicSocial Feed</h1>
        <p className="text-gray-600">Share your thoughts and engage with the community</p>
      </div>

      {/* Create Post */}
      <Card className="mb-6">
        <CardContent className="p-6">
          <div className="flex items-start space-x-4">
            <Avatar className="h-10 w-10">
              <AvatarImage src={user?.profileImageUrl} />
              <AvatarFallback>
                {user?.firstName?.[0] || user?.lastName?.[0] || user?.email?.[0] || 'U'}
              </AvatarFallback>
            </Avatar>
            <div className="flex-1">
              <Textarea
                placeholder="What's on your mind?"
                value={newPostContent}
                onChange={(e) => setNewPostContent(e.target.value)}
                className="min-h-[100px] resize-none"
                ref={textareaRef}
              />
              <div className="flex items-center justify-between mt-4">
                <div className="flex items-center space-x-2">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => setIsCreatePostOpen(true)}
                  >
                    <ImageIcon className="w-4 h-4 mr-2" />
                    Add Image
                  </Button>
                  <Button
                    variant="outline"
                    size="sm"
                  >
                    <Smile className="w-4 h-4 mr-2" />
                    Emoji
                  </Button>
                </div>
                <Button
                  onClick={handleCreatePost}
                  disabled={createPostMutation.isPending || (!newPostContent.trim() && !imageUrl.trim())}
                >
                  <Send className="w-4 h-4 mr-2" />
                  Post
                </Button>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Posts Feed */}
      <div className="space-y-6">
        {isLoading ? (
          <div className="animate-pulse space-y-6">
            {[...Array(3)].map((_, i) => (
              <Card key={i}>
                <CardContent className="p-6">
                  <div className="h-4 bg-gray-200 rounded w-3/4 mb-2"></div>
                  <div className="h-3 bg-gray-200 rounded w-1/2"></div>
                </CardContent>
              </Card>
            ))}
          </div>
        ) : posts.length === 0 ? (
          <Card>
            <CardContent className="p-6">
              <div className="text-center">
                <h3 className="text-lg font-semibold mb-2">No posts yet</h3>
                <p className="text-gray-600">Be the first to share something!</p>
              </div>
            </CardContent>
          </Card>
        ) : (
          posts.map((post) => (
            <Card key={post.id}>
              <CardHeader className="pb-4">
                <div className="flex items-start justify-between">
                  <div className="flex items-center space-x-3">
                      <Avatar className="h-10 w-10">
                        <AvatarImage src={post.user?.profileImageUrl} />
                      <AvatarFallback>{getUserInitials(post)}</AvatarFallback>
                    </Avatar>
                    <div>
                      <p className="font-medium text-gray-900">{getUserDisplayName(post)}</p>
                      <p className="text-sm text-gray-500">
                        {formatDistanceToNow(new Date(post.createdAt), { addSuffix: true })}
                      </p>
                    </div>
                  </div>
                  {post.userId === user?.id && (
                    <div className="flex items-center space-x-2">
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => setEditingPost(post)}
                      >
                        <Edit className="w-4 h-4" />
                      </Button>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => handleDeletePost(post.id)}
                      >
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    </div>
                  )}
                </div>
              </CardHeader>

              <CardContent>
                {post.content && (
                  <p className="text-gray-900 mb-4">{post.content}</p>
                )}

                {post.imageUrl && (
                  <img 
                    src={post.imageUrl} 
                    alt="Post image" 
                    className="w-full h-64 object-cover rounded-lg mb-4"
                  />
                )}

                {post.type === 'share' && post.comment && (
                  <div className="bg-gray-50 p-4 rounded-lg mb-4">
                    <p className="text-sm text-gray-600">{post.comment}</p>
                  </div>
                )}

                <div className="flex items-center justify-between pt-4 border-t border-gray-100">
                  <div className="flex items-center space-x-4">
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => handleLike(post.id)}
                      className={post.isLiked ? "text-red-500" : ""}
                    >
                      <Heart className={`w-4 h-4 mr-2 ${post.isLiked ? "fill-current" : ""}`} />
                      {post.likeCount}
                    </Button>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => {
                        setSelectedPost(post);
                        setIsCommentDialogOpen(true);
                      }}
                    >
                      <MessageCircle className="w-4 h-4 mr-2" />
                      {post.commentCount}
                    </Button>
                    <Button variant="ghost" size="sm" onClick={() => sharePostMutation.mutate({ postId: post.id })}>
                      <Share2 className="w-4 h-4 mr-2" />
                      Share
                    </Button>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))
        )}
      </div>

      {/* Create Post Dialog */}
      <Dialog open={isCreatePostOpen} onOpenChange={setIsCreatePostOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Create Post</DialogTitle>
            <DialogDescription>
              Share your thoughts with the CivicOS community
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4">
            <Textarea
              placeholder="What's on your mind?"
              value={newPostContent}
              onChange={(e) => setNewPostContent(e.target.value)}
              className="min-h-[120px]"
            />
            <div className="space-y-2">
              <label className="text-sm font-medium">Image URL (optional)</label>
              <input
                type="url"
                placeholder="https://example.com/image.jpg"
                value={imageUrl}
                onChange={(e) => setImageUrl(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md"
              />
            </div>
            <div className="flex justify-end space-x-2">
              <Button variant="outline" onClick={() => setIsCreatePostOpen(false)}>
                Cancel
              </Button>
              <Button onClick={handleCreatePost}>
                Post
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>

      {/* Comments Dialog */}
      <Dialog open={isCommentDialogOpen} onOpenChange={setIsCommentDialogOpen}>
        <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Comments</DialogTitle>
            <DialogDescription>
              View and add comments to this post
            </DialogDescription>
          </DialogHeader>

          {selectedPost && (
            <div className="space-y-4">
              {/* Post preview */}
              <Card>
                <CardContent className="p-4">
                  <div className="flex items-center space-x-3 mb-3">
                      <Avatar className="h-8 w-8">
                        <AvatarImage src={selectedPost.user?.profileImageUrl} />
                      <AvatarFallback>{getUserInitials(selectedPost)}</AvatarFallback>
                    </Avatar>
                    <div>
                      <p className="font-medium text-sm">{getUserDisplayName(selectedPost)}</p>
                      <p className="text-xs text-gray-500">
                        {formatDistanceToNow(new Date(selectedPost.createdAt), { addSuffix: true })}
                      </p>
                    </div>
                  </div>
                  {selectedPost.content && <p className="text-sm">{selectedPost.content}</p>}
                </CardContent>
              </Card>

              {/* Comments (threaded) */}
              <div className="space-y-4">
                {renderComments(null, 0)}
              </div>

              {/* Add comment */}
              <div className="space-y-2">
                {replyToComment && (
                  <div className="bg-blue-50 p-3 rounded-lg">
                    <p className="text-sm text-blue-600">
                      Replying to {replyToComment.firstName && replyToComment.lastName 
                        ? `${replyToComment.firstName} ${replyToComment.lastName}` 
                        : replyToComment.userId}
                    </p>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => setReplyToComment(null)}
                    >
                      Cancel reply
                    </Button>
                  </div>
                )}
                <div className="flex space-x-2">
                  <Textarea
                    placeholder={replyToComment ? "Write a reply..." : "Write a comment..."}
                    value={newCommentContent}
                    onChange={(e) => setNewCommentContent(e.target.value)}
                    className="flex-1"
                  />
                  <Button
                    onClick={handleCreateComment}
                    disabled={!newCommentContent.trim()}
                  >
                    <Send className="w-4 h-4" />
                  </Button>
                </div>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/components/UnifiedSocialPost..content:
import React, { useState } from 'react';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Badge } from '@/components/ui/badge';
import { 
  MessageSquare, 
  Image as ImageIcon,
  MapPin,
  Tag,
  Smile,
  Send,
  Globe,
  Lock,
  Users,
  Share2
} from 'lucide-react';
import { useAuth } from '@/hooks/useAuth';
import { useToast } from '@/hooks/use-toast';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';

interface UnifiedSocialPostProps {
  onPostCreated?: () => void;
  placeholder?: string;
  showLocation?: boolean;
  showMood?: boolean;
  showTags?: boolean;
  showVisibility?: boolean;
  showImageUpload?: boolean;
  className?: string;
  autoFocus?: boolean;
}

export function UnifiedSocialPost({
  onPostCreated,
  placeholder = "What's on your mind?",
  showLocation = true,
  showMood = true,
  showTags = true,
  showVisibility = true,
  showImageUpload = true,
  className = "",
  autoFocus = false
}: UnifiedSocialPostProps) {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [content, setContent] = useState('');
  const [visibility, setVisibility] = useState<'public' | 'friends' | 'private'>('public');
  const [location, setLocation] = useState('');
  const [mood, setMood] = useState('');
  const [tags, setTags] = useState<string[]>([]);
  const [imageFile, setImageFile] = useState<File | null>(null);
  const [imagePreview, setImagePreview] = useState<string | null>(null);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const createPostMutation = useMutation({
    mutationFn: async (postData: any) => {
      return apiRequest('/api/social/posts', 'POST', postData);
    },
    onSuccess: () => {
      toast({
        title: "Post created!",
        description: "Your post has been shared to your profile and the feed.",
      });
      queryClient.invalidateQueries({ queryKey: ['social-posts'] });
      queryClient.invalidateQueries({ queryKey: ['user-posts'] });
      resetForm();
      onPostCreated?.();
    },
    onError: (error: any) => {
      toast({
        title: "Post failed",
        description: error.message || "Failed to create post. Please try again.",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setContent('');
    setVisibility('public');
    setLocation('');
    setMood('');
    setTags([]);
    setImageFile(null);
    setImagePreview(null);
  };

  const handleImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      setImageFile(file);
      setImagePreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!content.trim() && !imageFile) {
      toast({
        title: "Content required",
        description: "Please enter some content or upload an image.",
        variant: "destructive",
      });
      return;
    }

    if (!user) {
      toast({
        title: "Login required",
        description: "Please log in to create posts.",
        variant: "destructive",
      });
      return;
    }

    setIsSubmitting(true);

    try {
      // Upload image if present
      let imageUrl = null;
      if (imageFile) {
        const formData = new FormData();
        formData.append('image', imageFile);

        const uploadResponse = await apiRequest('/api/upload/image', 'POST', formData);

        imageUrl = uploadResponse.imageUrl;
      }

      // Create post
      const postData = {
        content: content.trim(),
        type: 'post',
        visibility,
        imageUrl,
        location: location.trim() || undefined,
        mood: mood.trim() || undefined,
        tags: tags.filter(tag => tag.trim()),
      };

      createPostMutation.mutate(postData);
    } catch (error) {
      // console.error removed for production
      toast({
        title: "Post failed",
        description: "Failed to create post. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleTagInput = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter' || e.key === ',') {
      e.preventDefault();
      const value = e.currentTarget.value.trim();
      if (value && !tags.includes(value)) {
        setTags([...tags, value]);
        e.currentTarget.value = '';
      }
    }
  };

  const removeTag = (tagToRemove: string) => {
    setTags(tags.filter(tag => tag !== tagToRemove));
  };

  const getVisibilityIcon = () => {
    switch (visibility) {
      case 'public': return <Globe className="w-4 h-4" />;
      case 'friends': return <Users className="w-4 h-4" />;
      case 'private': return <Lock className="w-4 h-4" />;
      default: return <Globe className="w-4 h-4" />;
    }
  };

  if (!user) {
    return (
      <Card className={`p-4 ${className}`}>
        <CardContent className="text-center py-8">
          <MessageSquare className="w-12 h-12 text-gray-400 mx-auto mb-4" />
          <h3 className="text-lg font-semibold mb-2">Login to Post</h3>
          <p className="text-gray-600 mb-4">
            Join the conversation by logging in to your CivicOS account.
          </p>
          <Button onClick={() => window.location.href = '/auth'}>
            Login to Post
          </Button>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className={`p-4 ${className}`}>
      <CardContent className="p-0">
        <form onSubmit={handleSubmit} className="space-y-4">
          {/* User info and post input */}
          <div className="flex items-start gap-3">
            <Avatar className="w-10 h-10">
              <AvatarImage src={user.profileImageUrl} />
              <AvatarFallback className="bg-blue-600">
                {user.firstName?.[0] || user.email?.[0] || 'U'}
              </AvatarFallback>
            </Avatar>

            <div className="flex-1 space-y-3">
              <Textarea
                placeholder={placeholder}
                value={content}
                onChange={(e) => setContent(e.target.value)}
                className="min-h-[100px] resize-none border-0 focus:ring-0 p-0 text-base"
                autoFocus={autoFocus}
                maxLength={1000}
              />

              {/* Image preview */}
              {imagePreview && (
                <div className="relative inline-block">
                  <img 
                    src={imagePreview} 
                    alt="Preview" 
                    className="max-w-xs max-h-48 rounded-lg object-cover"
                  />
                  <Button
                    type="button"
                    variant="destructive"
                    size="sm"
                    className="absolute -top-2 -right-2 w-6 h-6 p-0"
                    onClick={() => {
                      setImageFile(null);
                      setImagePreview(null);
                    }}
                  >
                    Ã—
                  </Button>
                </div>
              )}
            </div>
          </div>

          {/* Additional options */}
          <div className="space-y-3">
            {/* Tags */}
            {showTags && (
              <div className="space-y-2">
                <div className="flex items-center gap-2">
                  <Tag className="w-4 h-4 text-gray-500" />
                  <input
                    type="text"
                    placeholder="Add tags (press Enter or comma)"
                    onKeyDown={handleTagInput}
                    className="flex-1 text-sm border-0 focus:ring-0 p-0 bg-transparent"
                  />
                </div>
                {tags.length > 0 && (
                  <div className="flex flex-wrap gap-2">
                    {tags.map((tag, index) => (
                      <Badge key={index} variant="secondary" className="text-xs">
                        #{tag}
                        <button
                          type="button"
                          onClick={() => removeTag(tag)}
                          className="ml-1 hover:text-red-600"
                        >
                          Ã—
                        </button>
                      </Badge>
                    ))}
                  </div>
                )}
              </div>
            )}

            {/* Location and Mood */}
            <div className="flex gap-4">
              {showLocation && (
                <div className="flex items-center gap-2 flex-1">
                  <MapPin className="w-4 h-4 text-gray-500" />
                  <input
                    type="text"
                    placeholder="Add location"
                    value={location}
                    onChange={(e) => setLocation(e.target.value)}
                    className="flex-1 text-sm border-0 focus:ring-0 p-0 bg-transparent"
                  />
                </div>
              )}

              {showMood && (
                <div className="flex items-center gap-2 flex-1">
                  <Smile className="w-4 h-4 text-gray-500" />
                  <input
                    type="text"
                    placeholder="How are you feeling?"
                    value={mood}
                    onChange={(e) => setMood(e.target.value)}
                    className="flex-1 text-sm border-0 focus:ring-0 p-0 bg-transparent"
                  />
                </div>
              )}
            </div>

            {/* Action bar */}
            <div className="flex items-center justify-between pt-3 border-t">
              <div className="flex items-center gap-2">
                {showImageUpload && (
                  <label className="cursor-pointer">
                    <ImageIcon className="w-5 h-5 text-gray-500 hover:text-blue-600" />
                    <input
                      type="file"
                      accept="image/*"
                      onChange={handleImageChange}
                      className="hidden"
                    />
                  </label>
                )}

                {showVisibility && (
                  <select
                    value={visibility}
                    onChange={(e) => setVisibility(e.target.value as any)}
                    className="text-sm border-0 focus:ring-0 p-0 bg-transparent"
                  >
                    <option value="public">Public</option>
                    <option value="friends">Friends</option>
                    <option value="private">Private</option>
                  </select>
                )}
              </div>

              <div className="flex items-center gap-2">
                <span className="text-xs text-gray-500">
                  {content.length}/1000
                </span>
                <Button
                  type="submit"
                  disabled={(!content.trim() && !imageFile) || isSubmitting}
                  size="sm"
                >
                  <Send className="w-4 h-4 mr-2" />
                  {isSubmitting ? 'Posting...' : 'Post'}
                </Button>
              </div>
            </div>
          </div>
        </form>
      </CardContent>
    </Card>
  );
}

client/src/components/UserProfile.tsx.content:
import React, { useState, useEffect } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  User, 
  MapPin, 
  Calendar, 
  Users, 
  MessageCircle, 
  Heart, 
  Share2,
  Edit,
  Settings,
  Shield,
  Award,
  Globe,
  Building,
  Mail,
  Phone,
  ExternalLink,
  Activity
} from "lucide-react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { formatDistanceToNow } from "date-fns";
import { useLocation } from "wouter";

interface UserProfileData {
  id: string;
  firstName: string;
  lastName: string;
  email: string;
  profileImageUrl?: string;
  civicLevel: string;
  isVerified: boolean;
  createdAt: string;
  city?: string;
  province?: string;
  country?: string;
  displayName: string;
}

interface UserStats {
  postsCount: number;
  friendsCount: number;
}

interface UserProfileResponse {
  profile: UserProfileData;
  stats: UserStats;
  friendshipStatus?: string;
}

interface SocialPost {
  id: number;
  content: string;
  imageUrl?: string;
  type: string;
  createdAt: string;
  updatedAt: string;
  likeCount: number;
  commentsCount: number;
  sharesCount: number;
  isLiked: boolean;
  isBookmarked: boolean;
}

export function UserProfile() {
  const [location, setLocation] = useLocation();
  const { user: currentUser, isAuthenticated } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Extract userId from URL path
  const userId = location.split('/').pop() || currentUser?.id;

  const [isEditMode, setIsEditMode] = useState(false);
  const [editForm, setEditForm] = useState({
    firstName: "",
    lastName: "",
    city: "",
    province: "",
    country: ""
  });

  // Fetch user profile
  const { data: profileData, isLoading } = useQuery<UserProfileResponse>({
    queryKey: ["userProfile", userId],
    queryFn: async () => {
      const response = await apiRequest(`/api/social/profile/${userId}`, "GET");
      return response;
    },
    enabled: !!userId,
  });

  // Fetch user's posts
  const { data: userPosts = [] } = useQuery<SocialPost[]>({
    queryKey: ["userPosts", userId],
    queryFn: async () => {
      const response = await apiRequest(`/api/social/posts?userId=${userId}`, "GET");
      return response?.posts || [];
    },
    enabled: !!userId && isAuthenticated,
  });

  // Update profile mutation
  const updateProfileMutation = useMutation({
    mutationFn: async (data: typeof editForm) => {
      return apiRequest("/api/social/profile", "PUT", data);
    },
    onSuccess: () => {
      toast({
        title: "Profile Updated",
        description: "Your profile has been updated successfully.",
      });
      setIsEditMode(false);
      queryClient.invalidateQueries({ queryKey: ["userProfile", userId] });
    },
    onError: () => {
      toast({
        title: "Update Failed",
        description: "Failed to update profile. Please try again.",
        variant: "destructive",
      });
    },
  });

  // Friend actions mutation
  const friendActionMutation = useMutation({
    mutationFn: async (action: string) => {
      return apiRequest("/api/social/friends", "POST", {
        friendId: userId,
        action
      });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["userProfile", userId] });
      queryClient.invalidateQueries({ queryKey: ["friends"] });
    },
  });

  // Initialize edit form when profile data loads
  useEffect(() => {
    if (profileData?.profile) {
      setEditForm({
        firstName: profileData.profile.firstName,
        lastName: profileData.profile.lastName,
        city: profileData.profile.city || "",
        province: profileData.profile.province || "",
        country: profileData.profile.country || ""
      });
    }
  }, [profileData]);

  const handleUpdateProfile = () => {
    updateProfileMutation.mutate(editForm);
  };

  const handleFriendAction = (action: string) => {
    friendActionMutation.mutate(action);
  };

  const isOwnProfile = currentUser?.id === userId;
  const canEdit = isOwnProfile && isAuthenticated;

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    );
  }

  if (!profileData) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <div className="text-center">
          <User className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
          <h3 className="text-lg font-semibold">User Not Found</h3>
          <p className="text-muted-foreground">The user you're looking for doesn't exist.</p>
        </div>
      </div>
    );
  }

  const { profile, stats, friendshipStatus } = profileData;

  return (
    <div className="max-w-4xl mx-auto p-6 space-y-6">
      {/* Profile Header */}
      <Card>
        <CardHeader className="relative">
          <div className="flex items-start justify-between">
            <div className="flex items-center space-x-4">
              <Avatar className="h-20 w-20">
                <AvatarImage src={profile.profileImageUrl} />
                <AvatarFallback className="text-lg">
                  {profile.firstName?.[0]}{profile.lastName?.[0]}
                </AvatarFallback>
              </Avatar>
              <div className="space-y-2">
                <div className="flex items-center space-x-2">
                  <h1 className="text-2xl font-bold">{profile.displayName}</h1>
                  {profile.isVerified && (
                    <Badge variant="secondary" className="bg-green-100 text-green-800">
                      <Shield className="h-3 w-3 mr-1" />
                      Verified
                    </Badge>
                  )}
                  <Badge variant="outline">
                    <Award className="h-3 w-3 mr-1" />
                    {profile.civicLevel}
                  </Badge>
                </div>
                <div className="flex items-center space-x-4 text-sm text-muted-foreground">
                  {profile.city && (
                    <div className="flex items-center">
                      <MapPin className="h-4 w-4 mr-1" />
                      {profile.city}, {profile.province}
                    </div>
                  )}
                  <div className="flex items-center">
                    <Calendar className="h-4 w-4 mr-1" />
                    Member since {formatDistanceToNow(new Date(profile.createdAt))} ago
                  </div>
                </div>
              </div>
            </div>
            <div className="flex items-center space-x-2">
              {canEdit ? (
                <Button
                  variant="outline"
                  onClick={() => setIsEditMode(true)}
                >
                  <Edit className="h-4 w-4 mr-2" />
                  Edit Profile
                </Button>
              ) : isAuthenticated && !isOwnProfile ? (
                <div className="flex space-x-2">
                  {friendshipStatus === "accepted" ? (
                    <Button variant="outline" disabled>
                      <Users className="h-4 w-4 mr-2" />
                      Friends
                    </Button>
                  ) : friendshipStatus === "pending" ? (
                    <Button variant="outline" disabled>
                      Request Sent
                    </Button>
                  ) : (
                    <Button
                      onClick={() => handleFriendAction("send_request")}
                      disabled={friendActionMutation.isPending}
                    >
                      <Users className="h-4 w-4 mr-2" />
                      Add Friend
                    </Button>
                  )}
                  <Button
                    variant="outline"
                    onClick={() => setLocation(`/messages/${userId}`)}
                  >
                    <MessageCircle className="h-4 w-4 mr-2" />
                    Message
                  </Button>
                </div>
              ) : null}
            </div>
          </div>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="text-center">
              <div className="text-2xl font-bold text-primary">{stats.postsCount}</div>
              <div className="text-sm text-muted-foreground">Posts</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-primary">{stats.friendsCount}</div>
              <div className="text-sm text-muted-foreground">Friends</div>
            </div>
            <div className="text-center">
              <div className="text-2xl font-bold text-primary">{profile.civicLevel}</div>
              <div className="text-sm text-muted-foreground">Civic Level</div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Profile Content Tabs */}
      <Tabs defaultValue="posts" className="space-y-4">
        <TabsList className="grid w-full grid-cols-3">
          <TabsTrigger value="posts">Posts</TabsTrigger>
          <TabsTrigger value="about">About</TabsTrigger>
          <TabsTrigger value="activity">Activity</TabsTrigger>
        </TabsList>

        <TabsContent value="posts" className="space-y-4">
          {userPosts.length === 0 ? (
            <Card>
              <CardContent className="flex items-center justify-center py-8">
                <div className="text-center">
                  <MessageCircle className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                  <h3 className="text-lg font-semibold">No Posts Yet</h3>
                  <p className="text-muted-foreground">
                    {isOwnProfile ? "Share your thoughts with the community!" : "This user hasn't posted anything yet."}
                  </p>
                </div>
              </CardContent>
            </Card>
          ) : (
            <div className="space-y-4">
              {userPosts.map((post) => (
                <Card key={post.id}>
                  <CardContent className="p-4">
                    <div className="flex items-start space-x-3">
                      <Avatar className="h-10 w-10">
                        <AvatarImage src={profile.profileImageUrl} />
                        <AvatarFallback>
                          {profile.firstName?.[0]}{profile.lastName?.[0]}
                        </AvatarFallback>
                      </Avatar>
                      <div className="flex-1 space-y-2">
                        <div className="flex items-center justify-between">
                          <div className="flex items-center space-x-2">
                            <span className="font-semibold">{profile.displayName}</span>
                            <span className="text-sm text-muted-foreground">
                              {formatDistanceToNow(new Date(post.createdAt))} ago
                            </span>
                          </div>
                        </div>
                        <p className="text-sm">{post.content}</p>
                        {post.imageUrl && (
                          <img 
                            src={post.imageUrl} 
                            alt="Post" 
                            className="rounded-lg max-w-full h-auto"
                          />
                        )}
                        <div className="flex items-center space-x-4 text-sm text-muted-foreground">
                          <button className="flex items-center space-x-1 hover:text-primary">
                            <Heart className="h-4 w-4" />
                            <span>{post.likeCount}</span>
                          </button>
                          <button className="flex items-center space-x-1 hover:text-primary">
                            <MessageCircle className="h-4 w-4" />
                            <span>{post.commentsCount}</span>
                          </button>
                          <button className="flex items-center space-x-1 hover:text-primary">
                            <Share2 className="h-4 w-4" />
                            <span>{post.sharesCount}</span>
                          </button>
                        </div>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </TabsContent>

        <TabsContent value="about" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>About</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label className="text-sm font-medium text-muted-foreground">Location</Label>
                  <div className="flex items-center space-x-2">
                    <MapPin className="h-4 w-4 text-muted-foreground" />
                    <span>
                      {profile.city && profile.province 
                        ? `${profile.city}, ${profile.province}, ${profile.country}`
                        : "Not specified"
                      }
                    </span>
                  </div>
                </div>
                <div className="space-y-2">
                  <Label className="text-sm font-medium text-muted-foreground">Member Since</Label>
                  <div className="flex items-center space-x-2">
                    <Calendar className="h-4 w-4 text-muted-foreground" />
                    <span>{new Date(profile.createdAt).toLocaleDateString()}</span>
                  </div>
                </div>
                <div className="space-y-2">
                  <Label className="text-sm font-medium text-muted-foreground">Civic Level</Label>
                  <div className="flex items-center space-x-2">
                    <Award className="h-4 w-4 text-muted-foreground" />
                    <span>{profile.civicLevel}</span>
                  </div>
                </div>
                <div className="space-y-2">
                  <Label className="text-sm font-medium text-muted-foreground">Verification</Label>
                  <div className="flex items-center space-x-2">
                    <Shield className="h-4 w-4 text-muted-foreground" />
                    <span>{profile.isVerified ? "Verified" : "Not verified"}</span>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="activity" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Recent Activity</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-center py-8">
                <Activity className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                <h3 className="text-lg font-semibold">Activity Tracking</h3>
                <p className="text-muted-foreground">
                  Activity tracking will be available soon.
                </p>
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>

      {/* Edit Profile Dialog */}
      <Dialog open={isEditMode} onOpenChange={setIsEditMode}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>Edit Profile</DialogTitle>
            <DialogDescription>
              Update your profile information and personal details.
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="firstName">First Name</Label>
                <Input
                  id="firstName"
                  value={editForm.firstName}
                  onChange={(e) => setEditForm(prev => ({ ...prev, firstName: e.target.value }))}
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="lastName">Last Name</Label>
                <Input
                  id="lastName"
                  value={editForm.lastName}
                  onChange={(e) => setEditForm(prev => ({ ...prev, lastName: e.target.value }))}
                />
              </div>
            </div>
            <div className="space-y-2">
              <Label htmlFor="city">City</Label>
              <Input
                id="city"
                value={editForm.city}
                onChange={(e) => setEditForm(prev => ({ ...prev, city: e.target.value }))}
              />
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="province">Province</Label>
                <Input
                  id="province"
                  value={editForm.province}
                  onChange={(e) => setEditForm(prev => ({ ...prev, province: e.target.value }))}
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="country">Country</Label>
                <Input
                  id="country"
                  value={editForm.country}
                  onChange={(e) => setEditForm(prev => ({ ...prev, country: e.target.value }))}
                />
              </div>
            </div>
            <div className="flex justify-end space-x-2">
              <Button
                variant="outline"
                onClick={() => setIsEditMode(false)}
              >
                Cancel
              </Button>
              <Button
                onClick={handleUpdateProfile}
                disabled={updateProfileMutation.isPending}
              >
                {updateProfileMutation.isPending ? "Saving..." : "Save Changes"}
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

