import { useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { ThumbsUp, ThumbsDown } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { cn } from "@/lib/utils";

interface VotingButtonsProps {
  targetType: 'politician' | 'bill' | 'post' | 'comment' | 'petition' | 'news' | 'finance';
  targetId: number;
  className?: string;
  size?: 'sm' | 'md' | 'lg';
  showCounts?: boolean;
}

export function VotingButtons({ 
  targetType, 
  targetId, 
  className = "",
  size = 'md',
  showCounts = true 
}: VotingButtonsProps) {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: voteData, isLoading } = useQuery({
    queryKey: [`/api/vote/${targetType}/${targetId}`],
    enabled: !!targetId
  });

  const voteMutation = useMutation({
    mutationFn: async (voteType: 'upvote' | 'downvote') => {
      const res = await apiRequest("/api/vote", "POST", {
        targetType,
        targetId,
        voteType
      });

      return await res.json();
    },
    onSuccess: (data) => {
      queryClient.setQueryData([`/api/vote/${targetType}/${targetId}`], data);
      queryClient.invalidateQueries({ queryKey: [`/api/${targetType}s`] });
      queryClient.invalidateQueries({ queryKey: ["/api/forum/posts"] });
      queryClient.invalidateQueries({ queryKey: ["/api/politicians"] });
      queryClient.invalidateQueries({ queryKey: ["/api/bills"] });
    },
    onError: (error: any) => {
      // console.error removed for production

      // Parse error message from API response
      let errorMessage = "Failed to register vote";
      if (error.message) {
        if (error.message.includes("already voted")) {
          toast({
            title: "Already Voted",
            description: "You have already voted on this item. Each user can only vote once.",
            variant: "default",
          });
          return;
        }
        // Extract message from "400: {message: ...}" format
        const match = error.message.match(/400: (.+)/);
        if (match) {
          try {
            const parsed = JSON.parse(match[1]);
            errorMessage = parsed.message || errorMessage;
          } catch {
            errorMessage = match[1];
          }
        } else {
          errorMessage = error.message;
        }
      }

      toast({
        title: "Vote Failed",
        description: errorMessage,
        variant: "destructive",
      });
    },
  });

  const handleVote = (voteType: 'upvote' | 'downvote') => {
    if (!user) {
      toast({
        title: "Login Required",
        description: "Please log in to vote",
        variant: "destructive",
      });
      return;
    }

    voteMutation.mutate(voteType);
  };

  const upvotes = (voteData as any)?.upvotes || 0;
  const downvotes = (voteData as any)?.downvotes || 0;
  const totalScore = (voteData as any)?.totalScore || 0;
  const userVote = (voteData as any)?.userVote;

  const buttonSizes = {
    sm: "h-7 w-7",
    md: "h-8 w-8", 
    lg: "h-10 w-10"
  };

  const iconSizes = {
    sm: "w-3 h-3",
    md: "w-4 h-4",
    lg: "w-5 h-5"
  };

  return (
    <div className={cn("flex items-center space-x-2", className)}>
      <div className="flex items-center space-x-1">
        <Button
          variant={userVote === 'upvote' ? "default" : "outline"}
          size="sm"
          className={cn(
            buttonSizes[size],
            "p-0",
            userVote === 'upvote' && "bg-green-600 hover:bg-green-700 text-white"
          )}
          onClick={() => handleVote('upvote')}
          disabled={voteMutation.isPending || isLoading}
        >
          <ThumbsUp className={iconSizes[size]} />
        </Button>
        {showCounts && (
          <span className="text-sm font-medium text-green-600 min-w-[20px] text-center">
            {upvotes}
          </span>
        )}
      </div>

      <div className="flex items-center space-x-1">
        <Button
          variant={userVote === 'downvote' ? "default" : "outline"}
          size="sm"
          className={cn(
            buttonSizes[size],
            "p-0",
            userVote === 'downvote' && "bg-red-600 hover:bg-red-700 text-white"
          )}
          onClick={() => handleVote('downvote')}
          disabled={voteMutation.isPending || isLoading}
        >
          <ThumbsDown className={iconSizes[size]} />
        </Button>
        {showCounts && (
          <span className="text-sm font-medium text-red-600 min-w-[20px] text-center">
            {downvotes}
          </span>
        )}
      </div>

      {showCounts && (
        <div className="flex items-center space-x-1 ml-2 pl-2 border-l">
          <span className="text-xs text-muted-foreground">Score:</span>
          <span className={cn(
            "text-sm font-bold",
            totalScore > 0 && "text-green-600",
            totalScore < 0 && "text-red-600",
            totalScore === 0 && "text-gray-500"
          )}>
            {totalScore > 0 ? `+${totalScore}` : totalScore}
          </span>
        </div>
      )}
    </div>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

client/src/hooks/useAuth.ts.content:
import React, { createContext, useContext, useState, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';

export interface User {
  id: string;
  email: string;
  username: string;
  firstName?: string;
  lastName?: string;
  profileImageUrl?: string;
  bio?: string;
  city?: string;
  province?: string;
  civicLevel?: string;
  trustScore?: number;
  isVerified?: boolean;
  isAdmin?: boolean;
  verificationStatus?: string;
  createdAt?: string;
  updatedAt?: string;
  followersCount?: number;
  followingCount?: number;
  postsCount?: number;
  civicPoints?: number;
}

interface AuthContextType {
  user: User | null;
  isAuthenticated: boolean;
  isLoading: boolean;
  login: (credentials: { email: string; password: string }) => Promise<User>;
  register: (userData: { email: string; password: string; firstName: string; lastName: string }) => Promise<User>;
  logout: () => void;
  updateProfile: (updates: Partial<User>) => Promise<User>;
  createUserProfile: (userData: Partial<User>) => Promise<User>;
  refreshUser: () => Promise<void>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export function AuthProvider({ children }: { children: React.ReactNode }) {
  const queryClient = useQueryClient();
  const [isLoading, setIsLoading] = useState(true);

  // Unified user query with proper error handling
  const { 
    data: user, 
    isLoading: isUserLoading, 
    error: userError,
    refetch: refreshUser 
  } = useQuery({
    queryKey: ['auth-user'],
    queryFn: async () => {
      const token = localStorage.getItem('civicos-jwt');
      if (!token) {
        throw new Error('No token found');
      }

      try {
        const response = await apiRequest('/api/auth/user', 'GET');

        if (!response || !response.id) {
          throw new Error('Invalid user data');
        }

        return response as User;
      } catch (error) {
        // Clear invalid token
        localStorage.removeItem('civicos-jwt');
        throw error;
      }
    },
    retry: 1,
    retryDelay: 1000,
    staleTime: 5 * 60 * 1000, // 5 minutes
    gcTime: 10 * 60 * 1000, // 10 minutes
    enabled: false, // Disable by default, will be enabled when token is available
  });

  // Check for token and enable query when token is available
  useEffect(() => {
    const token = localStorage.getItem('civicos-jwt');
    if (token) {
      // Trigger the query when token is available
      refreshUser();
    } else {
      setIsLoading(false);
    }
  }, []);

  // Login mutation with server warmup support
  const loginMutation = useMutation({
    mutationFn: async (credentials: { email: string; password: string }) => {
      // Import warmup utility dynamically to avoid circular deps
      const { apiRequestWithWarmup } = await import('@/lib/serverWarmup');

      const response = await apiRequestWithWarmup('/api/auth/login', 'POST', credentials, {
        maxRetries: 10,
        initialDelay: 500,
        timeout: 30000, // 30 seconds total
      });

      if (!response.token) {
        throw new Error('No token received from server');
      }

      localStorage.setItem('civicos-jwt', response.token);
      return response.user as User;
    },
    onSuccess: (userData) => {
      queryClient.setQueryData(['auth-user'], userData);
      setIsLoading(false);
    },
    onError: () => {
      // console.error removed for production
      localStorage.removeItem('civicos-jwt');
      queryClient.setQueryData(['auth-user'], null);
      setIsLoading(false);
    },
  });

  // Register mutation with server warmup support
  const registerMutation = useMutation({
    mutationFn: async (userData: { 
      email: string; 
      password: string; 
      firstName: string; 
      lastName: string 
    }) => {
      // Import warmup utility dynamically to avoid circular deps
      const { apiRequestWithWarmup } = await import('@/lib/serverWarmup');

      const response = await apiRequestWithWarmup('/api/auth/register', 'POST', userData, {
        maxRetries: 10,
        initialDelay: 500,
        timeout: 30000, // 30 seconds total
      });

      if (!response.token) {
        throw new Error('No token received from server');
      }

      localStorage.setItem('civicos-jwt', response.token);
      return response.user as User;
    },
    onSuccess: (userData) => {
      queryClient.setQueryData(['auth-user'], userData);
      setIsLoading(false);
    },
    onError: () => {
      // console.error removed for production
      localStorage.removeItem('civicos-jwt');
      queryClient.setQueryData(['auth-user'], null);
      setIsLoading(false);
    },
  });

  // Update profile mutation
  const updateProfileMutation = useMutation({
    mutationFn: async (updates: Partial<User>) => {
      const response = await apiRequest('/api/users/profile', 'PUT', updates);
      return response as User;
    },
    onSuccess: (updatedUser) => {
      queryClient.setQueryData(['auth-user'], updatedUser);
    },
    onError: () => {
      // console.error removed for production
    },
  });

  // Logout function
  const logout = () => {
    localStorage.removeItem('civicos-jwt');
    queryClient.setQueryData(['auth-user'], null);
    queryClient.clear(); // Clear all cached data
    setIsLoading(false);
  };

  // Create user profile
  const createUserProfile = async (userData: Partial<User>): Promise<User> => {
    const response = await apiRequest('/api/users/profile', 'POST', userData);
    queryClient.setQueryData(['auth-user'], response);
    return response as User;
  };

  // Handle user error and loading state
  useEffect(() => {
    if (userError) {
      // console.error removed for production
      localStorage.removeItem('civicos-jwt');
      setIsLoading(false);
    }
  }, [userError]);

  // Handle loading state when query completes (success or error)
  useEffect(() => {
    if (!isUserLoading) {
      setIsLoading(false);
    }
  }, [isUserLoading]);

  const value: AuthContextType = {
    user: user || null,
    isAuthenticated: !!user,
    isLoading: isLoading || isUserLoading,
    login: loginMutation.mutateAsync,
    register: registerMutation.mutateAsync,
    logout,
    updateProfile: updateProfileMutation.mutateAsync,
    createUserProfile,
    refreshUser: async () => {
      await refreshUser();
    },
  };

  return React.createElement(AuthContext.Provider, { value }, children);
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

