import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { 
  Shield, 
  CheckCircle, 
  XCircle, 
  AlertTriangle, 
  Eye, 
  FileText,
  Download,
  Clock
} from "lucide-react";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface PendingVerification {
  id: string | number;
  userId: string;
  email: string;
  submittedAt: string;
  status: 'pending' | 'reviewing' | 'approved' | 'rejected';
  riskScore: number;
  flaggedReasons: string[];
  faceMatchScore?: number;
  documents: {
    idFront: string;
    idBack: string;
    selfie: string;
    livenessVideo?: string;
  };
  metadata: {
    ipAddress: string;
    userAgent: string;
    geolocation: string;
    duplicateChecks: {
      idHash: boolean;
      faceVector: boolean;
      ipMatch: boolean;
    };
  };
}

export default function IdentityReview() {
  const [selectedVerification, setSelectedVerification] = useState<PendingVerification | null>(null);
  const { toast } = useToast();

  // Fetch pending verifications
  const { data: pendingVerifications = [], isLoading } = useQuery<PendingVerification[]>({
    queryKey: ['/api/pending-verifications'],
    retry: false,
  });

  // Approve verification mutation
  const approveMutation = useMutation({
    mutationFn: async (verificationId: string) => {
      return apiRequest(`/api/admin/identity-verifications/${verificationId}/approve`, "POST");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/identity-verifications'] });
      toast({
        title: "Verification Approved",
        description: "User has been granted full platform access",
      });
      setSelectedVerification(null);
    },
    onError: (error: any) => {
      toast({
        title: "Approval Failed",
        description: error.message,
        variant: "destructive"
      });
    },
  });

  // Reject verification mutation
  const rejectMutation = useMutation({
    mutationFn: async ({ verificationId, reason }: { verificationId: string; reason: string }) => {
      return apiRequest(`/api/admin/identity-verifications/${verificationId}/reject`, "POST", { reason });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/identity-verifications'] });
      toast({
        title: "Verification Rejected",
        description: "User will need to re-submit verification",
      });
      setSelectedVerification(null);
    },
    onError: (error: any) => {
      toast({
        title: "Rejection Failed",
        description: error.message,
        variant: "destructive"
      });
    },
  });

  const getRiskBadge = (score: number) => {
    if (score >= 80) return <Badge variant="destructive">High Risk</Badge>;
    if (score >= 50) return <Badge variant="outline" className="border-yellow-500 text-yellow-700">Medium Risk</Badge>;
    return <Badge variant="default" className="bg-green-600">Low Risk</Badge>;
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'pending':
        return <Badge variant="outline"><Clock className="w-3 h-3 mr-1" />Pending</Badge>;
      case 'reviewing':
        return <Badge variant="outline" className="border-blue-500 text-blue-700"><Eye className="w-3 h-3 mr-1" />Reviewing</Badge>;
      case 'approved':
        return <Badge variant="default" className="bg-green-600"><CheckCircle className="w-3 h-3 mr-1" />Approved</Badge>;
      case 'rejected':
        return <Badge variant="destructive"><XCircle className="w-3 h-3 mr-1" />Rejected</Badge>;
      default:
        return <Badge variant="outline">Unknown</Badge>;
    }
  };

  if (isLoading) {
    return (
      <div className="p-6">
        <div className="animate-pulse space-y-4">
          <div className="h-8 bg-slate-200 rounded w-1/4"></div>
          <div className="h-64 bg-slate-200 rounded"></div>
        </div>
      </div>
    );
  }

  return (
    <div className="p-6">
      <div className="mb-6">
        <h1 className="text-2xl font-bold text-slate-900 flex items-center space-x-2">
          <Shield className="w-6 h-6" />
          <span>Identity Verification Review</span>
        </h1>
        <p className="text-slate-600 mt-1">
          Review and approve pending identity verifications for CivicOS access
        </p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Verification Queue */}
        <div className="lg:col-span-2">
          <Card>
            <CardHeader>
              <CardTitle>Pending Verifications ({pendingVerifications.length})</CardTitle>
            </CardHeader>
            <CardContent>
              {pendingVerifications.length === 0 ? (
                <div className="text-center py-8 text-slate-500">
                  <Shield className="w-12 h-12 mx-auto mb-4 opacity-50" />
                  <p>No pending verifications</p>
                </div>
              ) : (
                <div className="overflow-x-auto">
                  <Table>
                    <TableHeader>
                      <TableRow>
                        <TableHead>User</TableHead>
                        <TableHead>Submitted</TableHead>
                        <TableHead>Risk Score</TableHead>
                        <TableHead>Status</TableHead>
                        <TableHead>Actions</TableHead>
                      </TableRow>
                    </TableHeader>
                    <TableBody>
                      {pendingVerifications.map((verification: PendingVerification) => (
                        <TableRow 
                          key={verification.id}
                          className={selectedVerification?.id === verification.id ? "bg-blue-50" : ""}
                        >
                          <TableCell>
                            <div>
                              <p className="font-medium">{verification.email}</p>
                              <p className="text-sm text-slate-500">ID: {verification.userId}</p>
                            </div>
                          </TableCell>
                          <TableCell>
                            <div className="text-sm">
                              {new Date(verification.submittedAt).toLocaleDateString()}
                              <br />
                              {new Date(verification.submittedAt).toLocaleTimeString()}
                            </div>
                          </TableCell>
                          <TableCell>
                            {getRiskBadge(verification.riskScore)}
                            <br />
                            <span className="text-sm text-slate-500">{verification.riskScore}%</span>
                          </TableCell>
                          <TableCell>
                            {getStatusBadge(verification.status)}
                          </TableCell>
                          <TableCell>
                            <Button
                              variant="outline"
                              size="sm"
                              onClick={() => setSelectedVerification(verification)}
                            >
                              <Eye className="w-4 h-4 mr-1" />
                              Review
                            </Button>
                          </TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                </div>
              )}
            </CardContent>
          </Card>
        </div>

        {/* Review Panel */}
        <div className="lg:col-span-1">
          <Card>
            <CardHeader>
              <CardTitle>Verification Details</CardTitle>
            </CardHeader>
            <CardContent>
              {!selectedVerification ? (
                <div className="text-center py-8 text-slate-500">
                  <Eye className="w-12 h-12 mx-auto mb-4 opacity-50" />
                  <p>Select a verification to review</p>
                </div>
              ) : (
                <div className="space-y-6">
                  {/* User Info */}
                  <div>
                    <h4 className="font-semibold mb-2">User Information</h4>
                    <div className="space-y-1 text-sm">
                      <p><strong>Email:</strong> {selectedVerification.email}</p>
                      <p><strong>User ID:</strong> {selectedVerification.userId}</p>
                      <p><strong>Submitted:</strong> {new Date(selectedVerification.submittedAt).toLocaleString()}</p>
                      <p><strong>IP:</strong> {selectedVerification.metadata.ipAddress}</p>
                      <p><strong>Location:</strong> {selectedVerification.metadata.geolocation}</p>
                    </div>
                  </div>

                  {/* Risk Assessment */}
                  <div>
                    <h4 className="font-semibold mb-2">Risk Assessment</h4>
                    <div className="space-y-2">
                      {getRiskBadge(selectedVerification.riskScore)}
                      {selectedVerification.flaggedReasons.length > 0 && (
                        <div>
                          <p className="text-sm font-medium text-red-600 mb-1">Flagged Issues:</p>
                          <ul className="text-sm space-y-1">
                            {selectedVerification.flaggedReasons.map((reason, index) => (
                              <li key={index} className="flex items-center space-x-1">
                                <AlertTriangle className="w-3 h-3 text-red-500" />
                                <span>{reason}</span>
                              </li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  </div>

                  {/* Duplicate Checks */}
                  <div>
                    <h4 className="font-semibold mb-2">Duplicate Checks</h4>
                    <div className="space-y-1 text-sm">
                      <div className="flex items-center space-x-2">
                        {selectedVerification.metadata.duplicateChecks.idHash ? (
                          <XCircle className="w-4 h-4 text-red-500" />
                        ) : (
                          <CheckCircle className="w-4 h-4 text-green-500" />
                        )}
                        <span>ID Hash Check</span>
                      </div>
                      <div className="flex items-center space-x-2">
                        {selectedVerification.metadata.duplicateChecks.faceVector ? (
                          <XCircle className="w-4 h-4 text-red-500" />
                        ) : (
                          <CheckCircle className="w-4 h-4 text-green-500" />
                        )}
                        <span>Face Vector Check</span>
                      </div>
                      <div className="flex items-center space-x-2">
                        {selectedVerification.metadata.duplicateChecks.ipMatch ? (
                          <XCircle className="w-4 h-4 text-red-500" />
                        ) : (
                          <CheckCircle className="w-4 h-4 text-green-500" />
                        )}
                        <span>IP Match Check</span>
                      </div>
                    </div>
                  </div>

                  {/* Face Match Score */}
                  {selectedVerification.faceMatchScore && (
                    <div>
                      <h4 className="font-semibold mb-2">Face Verification</h4>
                      <div className="text-sm">
                        <p>Match Score: <strong>{selectedVerification.faceMatchScore.toFixed(1)}%</strong></p>
                        {selectedVerification.faceMatchScore >= 75 ? (
                          <Badge variant="default" className="bg-green-600 mt-1">
                            <CheckCircle className="w-3 h-3 mr-1" />
                            Match Verified
                          </Badge>
                        ) : (
                          <Badge variant="destructive" className="mt-1">
                            <AlertTriangle className="w-3 h-3 mr-1" />
                            Low Match Score
                          </Badge>
                        )}
                      </div>
                    </div>
                  )}

                  {/* Documents */}
                  <div>
                    <h4 className="font-semibold mb-2">Uploaded Documents</h4>
                    <div className="space-y-2">
                      <Button variant="outline" size="sm" className="w-full">
                        <FileText className="w-4 h-4 mr-2" />
                        View ID Front
                      </Button>
                      <Button variant="outline" size="sm" className="w-full">
                        <FileText className="w-4 h-4 mr-2" />
                        View ID Back
                      </Button>
                      <Button variant="outline" size="sm" className="w-full">
                        <FileText className="w-4 h-4 mr-2" />
                        View Selfie
                      </Button>
                      {selectedVerification.documents.livenessVideo && (
                        <Button variant="outline" size="sm" className="w-full">
                          <FileText className="w-4 h-4 mr-2" />
                          View Liveness Video
                        </Button>
                      )}
                    </div>
                  </div>

                  {/* Actions */}
                  <div className="space-y-2 pt-4 border-t">
                    <Button
                      onClick={() => approveMutation.mutate(selectedVerification.id.toString())}
                      disabled={approveMutation.isPending}
                      className="w-full bg-green-600 hover:bg-green-700"
                    >
                      <CheckCircle className="w-4 h-4 mr-2" />
                      Approve Verification
                    </Button>
                    <Button
                      onClick={() => rejectMutation.mutate({
                        verificationId: selectedVerification.id.toString(),
                        reason: "Failed manual review"
                      })}
                      disabled={rejectMutation.isPending}
                      variant="destructive"
                      className="w-full"
                    >
                      <XCircle className="w-4 h-4 mr-2" />
                      Reject Verification
                    </Button>
                  </div>
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

client/src/pages/admin/moderation.tsx.content:
import React, { useMemo, useState } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { createCivicOsClient } from '@/lib/civicos-sdk-wrapper';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { useToast } from '@/hooks/use-toast';
import { PermissionHint } from '@/components/PermissionHint';
import { useAuth } from '@/hooks/useAuth';

export default function AdminModerationPage() {
  const qc = useQueryClient();
  const { toast } = useToast();
  const client = createCivicOsClient();
  const { user } = useAuth();
  const [limit, setLimit] = useState(25);
  const [offset, setOffset] = useState(0);
  const [query, setQuery] = useState('');

  const { data: dashboard, isLoading, isError, error } = useQuery({
    queryKey: ['admin','moderation-dashboard', limit, offset],
    queryFn: async () => {
      const res = await client.request({ method: 'GET', url: `/api/admin/moderation-dashboard?limit=${limit}&offset=${offset}` });
      return res as any;
    },
    enabled: !!user?.isAdmin,
  });

  const removeComment = useMutation({
    mutationFn: async (id: number) => {
      await client.request({ method: 'DELETE', url: `/api/moderation/comments/${id}` });
    },
    onSuccess: () => {
      qc.invalidateQueries({ queryKey: ['admin','moderation-dashboard'] });
      toast({ title: 'Comment removed' });
    },
    onError: () => toast({ title: 'Failed to remove comment', variant: 'destructive' })
  });

  if (!user?.isAdmin) {
    return (
      <div className="p-6 space-y-4">
        <div>Access denied.</div>
        <PermissionHint required={["view_analytics", "reject_content", "moderate_comments"]} />
      </div>
    );
  }

  if (isLoading) return <div className="p-6">Loadingâ€¦</div>;
  if (isError) return <div className="p-6">Failed to load dashboard.</div>;

  const filteredPosts = useMemo(() => {
    const txt = query.trim().toLowerCase();
    if (!txt) return dashboard?.recent?.posts || [];
    return (dashboard?.recent?.posts || []).filter((p: any) => (p.content || '').toLowerCase().includes(txt));
  }, [dashboard, query]);

  const filteredComments = useMemo(() => {
    const txt = query.trim().toLowerCase();
    if (!txt) return dashboard?.recent?.comments || [];
    return (dashboard?.recent?.comments || []).filter((c: any) => (c.content || '').toLowerCase().includes(txt));
  }, [dashboard, query]);

  return (
    <div className="p-6 space-y-6">
      <div className="flex items-center gap-3">
        <input
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Filter posts/commentsâ€¦"
          className="w-full max-w-md border rounded px-3 py-2"
        />
        <div className="flex items-center gap-2">
          <Button variant="secondary" onClick={() => setOffset((o) => Math.max(0, o - limit))} disabled={offset === 0}>Prev</Button>
          <Button variant="secondary" onClick={() => setOffset((o) => o + limit)}>Next</Button>
        </div>
      </div>

      <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-6 gap-4">
        <Stat title="Users" value={dashboard?.summary?.users} />
        <Stat title="Posts" value={dashboard?.summary?.posts} />
        <Stat title="Comments" value={dashboard?.summary?.comments} />
        <Stat title="Notifications" value={dashboard?.summary?.notifications} />
        <Stat title="News" value={dashboard?.summary?.news} />
        <Stat title="Votes" value={dashboard?.summary?.votes} />
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Recent Posts</CardTitle>
        </CardHeader>
        <CardContent>
          <ul className="divide-y">
            {filteredPosts.map((p: any) => (
              <li key={p.id} className="py-3 flex items-center justify-between">
                <div className="min-w-0">
                  <div className="text-sm font-medium truncate">{p.content?.slice(0,120) || 'Untitled'}</div>
                  <div className="text-xs text-muted-foreground">{new Date(p.createdAt || p.created_at).toLocaleString()}</div>
                </div>
                {/* Placeholder remove post if needed later */}
              </li>
            ))}
          </ul>
        </CardContent>
      </Card>

      <Card>
        <CardHeader>
          <CardTitle>Recent Comments</CardTitle>
        </CardHeader>
        <CardContent>
          <ul className="divide-y">
            {filteredComments.map((c: any) => (
              <li key={c.id} className="py-3 flex items-center justify-between gap-4">
                <div className="min-w-0">
                  <div className="text-sm font-medium truncate">{c.content?.slice(0,160) || 'â€”'}</div>
                  <div className="text-xs text-muted-foreground">{new Date(c.createdAt || c.created_at).toLocaleString()}</div>
                </div>
                <Button variant="destructive" size="sm" onClick={() => removeComment.mutate(c.id)}>Remove</Button>
              </li>
            ))}
          </ul>
        </CardContent>
      </Card>
    </div>
  );
}

function Stat({ title, value }: { title: string; value: number }) {
  return (
    <Card>
      <CardHeader className="py-4">
        <CardTitle className="text-sm text-muted-foreground">{title}</CardTitle>
      </CardHeader>
      <CardContent className="pt-0 pb-4">
        <div className="text-2xl font-semibold">{value ?? 0}</div>
      </CardContent>
    </Card>
  );
}

client/src/pages/profile/[username].tsx.content:
import React, { useState } from 'react';
import { useLocation } from 'wouter';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { 
  User, 
  Calendar, 
  MapPin, 
  Mail, 
  Globe, 
  Share2, 
  Users, 
  MessageSquare,
  Heart,
  Activity,
  Award,
  Star,
  ExternalLink
} from 'lucide-react';
import { useQuery } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { useAuth } from '@/hooks/useAuth';
import { useToast } from '@/hooks/use-toast';
import { UnifiedSocialPost } from '@/components/UnifiedSocialPost';
import { useCivicSocialFollow } from '@/hooks/useCivicSocial';

interface UserProfile {
  id: string;
  username: string;
  firstName?: string;
  lastName?: string;
  email: string;
  bio?: string;
  profileImageUrl?: string;
  civicLevel?: string;
  isVerified?: boolean;
  location?: string;
  joinDate: string;
  postsCount: number;
  followersCount: number;
  followingCount: number;
  civicPoints: number;
  achievements: Array<{
    id: number;
    name: string;
    description: string;
    icon: string;
    earnedAt: string;
  }>;
}

interface SocialPost {
  id: number;
  content: string;
  imageUrl?: string;
  type: string;
  visibility: string;
  tags: string[];
  location?: string;
  mood?: string;
  createdAt: string;
  likesCount: number;
  commentsCount: number;
  sharesCount: number;
  isLiked: boolean;
  user: {
    id: string;
    firstName?: string;
    lastName?: string;
    profileImageUrl?: string;
  };
}

export default function PublicProfile() {
  const [location] = useLocation();
  const [, navigate] = useLocation();
  const param = location.split('/').pop(); // Works for /civicsocial/profile/:param and legacy routes
  const looksLikeUuid = /^[0-9a-fA-F-]{36}$/.test(param || '');
  const looksLikeDbId = looksLikeUuid || /^user_/i.test(param || '');
  const normalizedId = (param || '').replace(/^user_/i, '');
  const { user: currentUser } = useAuth();
  const { toast } = useToast();
  const [activeTab, setActiveTab] = useState('posts');
  const followMutation = useCivicSocialFollow();

  // Fetch user profile
  const { data: profile, isLoading: isLoadingProfile } = useQuery<UserProfile>({
    queryKey: ['user-profile', param],
    queryFn: async () => {
      if (looksLikeDbId) {
        const response = await apiRequest(`/api/users/${normalizedId}/profile`, 'GET');
        return response;
      }
      const response = await apiRequest(`/api/users/profile/${param}`, 'GET');
      return response.profile;
    },
    enabled: !!param,
  });

  // Fetch user posts
  const { data: posts, isLoading: isLoadingPosts } = useQuery<SocialPost[]>({
    queryKey: ['user-posts', profile?.username || param],
    queryFn: async () => {
      const handle = looksLikeDbId ? (profile?.username || '') : (param || '');
      if (!handle) return [];
      const response = await apiRequest(`/api/social/posts/user/${handle}`, 'GET');
      return response.posts || [];
    },
    enabled: !!(profile?.username || (!looksLikeDbId && param)),
  });

  // Fetch user achievements
  const { data: achievements } = useQuery({
    queryKey: ['user-achievements', profile?.username],
    queryFn: async () => {
      if (!profile?.username) return [];
      const response = await apiRequest(`/api/users/${profile.username}/achievements`, 'GET');
      return response.achievements || [];
    },
    enabled: !!profile?.username,
  });

  const handleShare = async () => {
    const shareUrl = `${window.location.origin}/civicsocial/profile/${profile?.username || param}`;
    const shareText = `Check out ${profile?.firstName || profile?.username}'s profile on CivicOS`;

    try {
      if (navigator.share) {
        await navigator.share({
          title: `${profile?.firstName || profile?.username}'s Profile`,
          text: shareText,
          url: shareUrl,
        });
      } else {
        await navigator.clipboard.writeText(`${shareText} ${shareUrl}`);
        toast({
          title: "Link copied!",
          description: "Profile link has been copied to your clipboard.",
        });
      }
    } catch (error) {
      // console.error removed for production
    }
  };

  const getDisplayName = () => {
    if (profile?.firstName && profile?.lastName) {
      return `${profile.firstName} ${profile.lastName}`;
    }
    if (profile?.firstName) return profile.firstName;
    if (profile?.username) return profile.username;
    return 'Anonymous User';
  };

  const getCivicLevelColor = (level: string) => {
    switch (level?.toLowerCase()) {
      case 'champion': return 'bg-purple-500';
      case 'expert': return 'bg-blue-500';
      case 'advocate': return 'bg-green-500';
      case 'active': return 'bg-yellow-500';
      default: return 'bg-gray-500';
    }
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  if (isLoadingProfile) {
    return (
      <div className="container mx-auto px-4 py-8 max-w-4xl">
        <div className="text-center py-12">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading profile...</p>
        </div>
      </div>
    );
  }

  if (!profile) {
    return (
      <div className="container mx-auto px-4 py-8 max-w-4xl">
        <div className="text-center py-12">
          <User className="w-16 h-16 text-gray-400 mx-auto mb-4" />
          <h2 className="text-2xl font-bold mb-2">Profile Not Found</h2>
          <p className="text-gray-600">The user profile you're looking for doesn't exist.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8 max-w-4xl">
      {/* Profile Header */}
      <Card className="mb-8">
        <CardContent className="p-6">
          <div className="flex items-start justify-between">
            <div className="flex items-center gap-6">
              <Avatar className="w-24 h-24">
                <AvatarImage src={profile.profileImageUrl} />
                <AvatarFallback className="bg-blue-600 text-2xl">
                  {getDisplayName()[0]?.toUpperCase()}
                </AvatarFallback>
              </Avatar>

              <div className="space-y-2">
                <div className="flex items-center gap-3">
                  <h1 className="text-3xl font-bold">{getDisplayName()}</h1>
                  {profile.isVerified && (
                    <Badge variant="secondary">
                      <Star className="w-3 h-3 mr-1" />
                      Verified
                    </Badge>
                  )}
                  <Badge className={`text-xs ${getCivicLevelColor(profile.civicLevel || '')}`}>
                    {profile.civicLevel || 'Registered'}
                  </Badge>
                </div>

                <p className="text-gray-600">@{profile.username}</p>

                {profile.bio && (
                  <p className="text-gray-700 max-w-md">{profile.bio}</p>
                )}

                <div className="flex items-center gap-4 text-sm text-gray-600">
                  {profile.location && (
                    <div className="flex items-center gap-1">
                      <MapPin className="w-4 h-4" />
                      {profile.location}
                    </div>
                  )}
                  <div className="flex items-center gap-1">
                    <Calendar className="w-4 h-4" />
                    Joined {formatDate(profile.joinDate)}
                  </div>
                </div>
              </div>
            </div>

            <div className="flex items-center gap-2">
              <Button variant="outline" size="sm" onClick={handleShare}>
                <Share2 className="w-4 h-4 mr-2" />
                Share Profile
              </Button>
              {currentUser?.id !== profile.id && (
                <>
                  <Button
                    variant="default"
                    size="sm"
                    onClick={() => navigate(`/civicsocial/messages?recipientId=${encodeURIComponent(profile.id)}`)}
                  >
                    <MessageSquare className="w-4 h-4 mr-2" />
                    Message
                  </Button>
                  <Button
                    variant="secondary"
                    size="sm"
                    onClick={() => followMutation.mutate({ followingId: profile.id }, {
                      onSuccess: () => toast({ title: 'Followed', description: `You are now following @${profile.username}` }),
                      onError: (e: any) => toast({ title: 'Follow failed', description: e?.message || 'Could not follow user', variant: 'destructive' })
                    })}
                  >
                    <Users className="w-4 h-4 mr-2" />
                    Follow
                  </Button>
                </>
              )}
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Stats */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-8">
        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-blue-600">{profile.postsCount}</div>
            <div className="text-sm text-gray-600">Posts</div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-green-600">{profile.followersCount}</div>
            <div className="text-sm text-gray-600">Followers</div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-purple-600">{profile.followingCount}</div>
            <div className="text-sm text-gray-600">Following</div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4 text-center">
            <div className="text-2xl font-bold text-orange-600">{profile.civicPoints}</div>
            <div className="text-sm text-gray-600">Civic Points</div>
          </CardContent>
        </Card>
      </div>

      {/* Tabs */}
      <Tabs value={activeTab} onValueChange={setActiveTab}>
        <TabsList className="grid w-full grid-cols-3">
          <TabsTrigger value="posts">Posts</TabsTrigger>
          <TabsTrigger value="achievements">Achievements</TabsTrigger>
          <TabsTrigger value="activity">Activity</TabsTrigger>
        </TabsList>

        <TabsContent value="posts" className="mt-6">
          <div className="space-y-6">
            {/* Create Post (if viewing own profile) */}
            {currentUser?.id === profile.id && (
              <UnifiedSocialPost 
                placeholder={`What's on your mind, ${profile.firstName || profile.username}?`}
                onPostCreated={() => {
                  // Refetch posts
                  window.location.reload();
                }}
              />
            )}

            {/* Posts List */}
            {isLoadingPosts ? (
              <div className="text-center py-8">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
                <p className="mt-2 text-gray-600">Loading posts...</p>
              </div>
            ) : posts?.length === 0 ? (
              <Card>
                <CardContent className="p-8 text-center">
                  <MessageSquare className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                  <h3 className="text-lg font-semibold mb-2">No posts yet</h3>
                  <p className="text-gray-600">
                    {currentUser?.id === profile.id 
                      ? "Share your first post to get started!"
                      : `${profile.firstName || profile.username} hasn't posted anything yet.`
                    }
                  </p>
                </CardContent>
              </Card>
            ) : (
              <div className="space-y-4">
                {posts?.map((post) => (
                  <Card key={post.id}>
                    <CardContent className="p-4">
                      <div className="flex items-start gap-3">
                        <Avatar className="w-10 h-10">
                          <AvatarImage src={post.user.profileImageUrl} />
                          <AvatarFallback className="bg-blue-600">
                            {post.user.firstName?.[0] || 'U'}
                          </AvatarFallback>
                        </Avatar>

                        <div className="flex-1">
                          <div className="flex items-center gap-2 mb-2">
                            <span className="font-semibold">
                              {post.user.firstName} {post.user.lastName}
                            </span>
                            <span className="text-sm text-gray-500">
                              {formatDate(post.createdAt)}
                            </span>
                          </div>

                          <p className="text-gray-800 mb-3">{post.content}</p>

                          {post.imageUrl && (
                            <img 
                              src={post.imageUrl} 
                              alt="Post" 
                              className="rounded-lg max-w-full h-auto mb-3"
                            />
                          )}

                          <div className="flex items-center gap-4 text-sm text-gray-500">
                            <button className="flex items-center gap-1 hover:text-blue-600">
                              <Heart className="w-4 h-4" />
                              {post.likesCount || 0}
                            </button>
                            <button className="flex items-center gap-1 hover:text-blue-600">
                              <MessageSquare className="w-4 h-4" />
                              {post.commentsCount || 0}
                            </button>
                            <button className="flex items-center gap-1 hover:text-blue-600">
                              <Share2 className="w-4 h-4" />
                              {post.sharesCount || 0}
                            </button>
                          </div>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </div>
        </TabsContent>

        <TabsContent value="achievements" className="mt-6">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {achievements?.length === 0 ? (
              <Card className="col-span-full">
                <CardContent className="p-8 text-center">
                  <Award className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                  <h3 className="text-lg font-semibold mb-2">No achievements yet</h3>
                  <p className="text-gray-600">
                    Keep engaging with civic activities to earn achievements!
                  </p>
                </CardContent>
              </Card>
            ) : (
              achievements?.map((achievement: any) => (
                <Card key={achievement.id}>
                  <CardContent className="p-4">
                    <div className="flex items-center gap-3">
                      <div className="p-2 bg-blue-100 rounded-lg">
                        <Award className="w-5 h-5 text-blue-600" />
                      </div>
                      <div>
                        <h4 className="font-semibold">{achievement.name}</h4>
                        <p className="text-sm text-gray-600">{achievement.description}</p>
                        <p className="text-xs text-gray-500 mt-1">
                          Earned {formatDate(achievement.earnedAt)}
                        </p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))
            )}
          </div>
        </TabsContent>

        <TabsContent value="activity" className="mt-6">
          <Card>
            <CardContent className="p-8 text-center">
              <Activity className="w-12 h-12 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-semibold mb-2">Activity Feed</h3>
              <p className="text-gray-600">
                Recent civic engagement activity will appear here.
              </p>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

